import { useState } from "react";

const Products = () => {
    const [selectedOption, setSelectedOption] = useState("");

    const handleSelectChange = (event) => {
        setSelectedOption(event.target.value);
        console.log("Selected:", event.target.value);
    };

    return (
        <div className="pt-5">
            <div className="flex justify-center">
                <h1 className="text-4xl font-charm">Plupare Products</h1>
            </div>
            <div className="flex flex-wrap justify-center items-center gap-5 p-5">
                <div className="flex w-full sm:w-2/3">
                    <input
                        type="text"
                        className="flex-grow border-2 border-gray-300 border-r-0 rounded-l-md p-2 text-black focus:outline-none focus:ring-2 focus:ring-gray-400"
                        placeholder="What are you looking for?"
                    />
                    <button
                        type="submit"
                        className="bg-gray-300 text-black px-4 py-2 rounded-r-xl border-l-2 border-gray-400 hover:scale-110 transform transition-transform duration-200 ease-in-out focus:ring-2 focus:ring-gray-400 focus:outline-none"
                    >
                        <img
                            src="https://cdn-icons-png.flaticon.com/128/775/775307.png"
                            alt="search icon"
                            height="30"
                            width="30"
                        />
                    </button>
                </div>

                <div className="p- w-full sm:w-auto">
                    <select
                        value={selectedOption}
                        onChange={handleSelectChange}
                        className="bg-black text-white font-semibold cursor-pointer p-2 rounded-md border-none focus:outline-none focus:ring-2 focus:ring-custom-pink"
                    >
                        <option value="" disabled>
                            Select Gender
                        </option>
                        <option value="men">Men</option>
                        <option value="women">Women</option>
                    </select>
                </div>
            </div>


            {/* product */}

            <div>

            </div>


        </div>
    );
};

export default Products;
