import { useState } from "react";

const Products = () => {
    const [selectedOption, setSelectedOption] = useState("");

    const handleSelectChange = (event) => {
        setSelectedOption(event.target.value);
        console.log("Selected:", event.target.value);
    };

    return (
        <div className="pt-5">
            <div className="flex justify-center">
                <h1 className="text-4xl font-charm">Plupare Products</h1>
            </div>
            <div className="flex justify-around mt-5">
                <div class="flex justify-center items-center min-h-screen bg-gray-100">
                    <div class="flex w-1/3">
                        <input
                            type="text"
                            class="flex-grow border-2 border-blue-500 border-r-0 rounded-l-md p-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
                            placeholder="What are you looking for?"
                        />
                        <button
                            type="submit"
                            class="bg-blue-500 text-white px-4 py-2 rounded-r-md hover:bg-blue-600 focus:ring-2 focus:ring-blue-400 focus:outline-none"
                        >
                            <i class="fa fa-search"></i>
                        </button>
                    </div>
                </div>

                <div><select
                    value={selectedOption}
                    onChange={handleSelectChange}
                    className="bg-black text-white font-semibold cursor-pointer p-2 rounded-md w-36 border-none focus:outline-none focus:ring-2 focus:ring-custom-pink"
                >
                    <option value="" disabled>
                        Select Gender
                    </option>
                    <option value="men">Men</option>
                    <option value="women">Women</option>
                </select>
                </div>

                <div className="mt-5 text-center">
                    {selectedOption && (
                        <p className="text-lg font-medium">
                            You selected: <span className="font-bold">{selectedOption}</span>
                        </p>
                    )}
                </div></div>

        </div>
    );
};

export default Products;
