import React, { useState, useRef, useEffect } from "react";
import { collection, getDocs, addDoc } from "firebase/firestore";
import { db } from "../../firebase/connection";

const Products = () => {
    const [products, setProducts] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [quantity, setQuantity] = useState(1);
    const [size, setSize] = useState("5ml");
    const [phone, setPhone] = useState("");
    const [userName, setUserName] = useState("");
    const [cart, setCart] = useState([]);
    const scrollerRef = useRef(null);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const querySnapshot = await getDocs(collection(db, "products"));
                const productsData = querySnapshot.docs.map((doc) => ({
                    id: doc.id,
                    ...doc.data(),
                }));
                setProducts(productsData);
                setFilteredProducts(productsData);
            } catch (error) {
                console.error("Error fetching products: ", error);
            }
        };

        fetchProducts();
    }, []);

    const handleSearchChange = (e) => {
        const value = e.target.value;
        setSearchTerm(value);

        if (value) {
            const filteredData = products.filter((product) =>
                product.name.toLowerCase().includes(value.toLowerCase())
            );
            setFilteredProducts(filteredData);
        } else {
            setFilteredProducts(products);
        }
    };

    const addToCart = () => {
        if (!userName.trim() || !phone.trim() || !selectedProduct) {
            alert("Please fill all required fields.");
            return;
        }

        const newCartItem = {
            id: selectedProduct.id,
            name: selectedProduct.name,
            size,
            quantity,
        };

        setCart((prevCart) => [...prevCart, newCartItem]);
        alert("Product added to cart!");

        setSelectedProduct(null);
        setQuantity(1);
        setSize("5ml");
        setUserName("");
        setPhone("");
    };

    const scrollLeft = () => {
        if (scrollerRef.current) {
            scrollerRef.current.scrollBy({ left: -200, behavior: "smooth" });
        }
    };

    const scrollRight = () => {
        if (scrollerRef.current) {
            scrollerRef.current.scrollBy({ left: 200, behavior: "smooth" });
        }
    };

    return (
        <div id="products" className="pt-5 px-4">
            <div className="flex justify-center">
                <h1 className="text-2xl sm:text-3xl lg:text-4xl font-charm text-center">
                    Plupare Products
                </h1>
            </div>

            <div className="flex w-[80%] mx-auto pt-5">
                <input
                    type="text"
                    value={searchTerm}
                    onChange={handleSearchChange}
                    className="flex-grow border-2 border-gray-300 rounded-l-md p-2 text-black focus:outline-none focus:ring-2 focus:ring-gray-400"
                    placeholder="Search for a product..."
                />
                <button className="bg-gray-300 px-4 py-2 rounded-r-md border-l-2 border-gray-400 hover:scale-110 transform transition-transform duration-200 ease-in-out focus:ring-2 focus:ring-gray-400 focus:outline-none">
                    <img
                        src="https://cdn-icons-png.flaticon.com/128/775/775307.png"
                        alt="Search Icon"
                        className="h-6 w-6"
                    />
                </button>
            </div>

            <div className="mt-10 relative">
                <div
                    ref={scrollerRef}
                    className="flex overflow-x-auto gap-6 scrollbar-hide py-4"
                    style={{
                        scrollbarWidth: "none",
                        msOverflowStyle: "none",
                    }}
                >
                    {filteredProducts.map((product) => (
                        <div
                            key={product.id}
                            className="min-w-[250px] flex-shrink-0 flex justify-center items-center flex-col shadow-lg rounded-lg p-6 border-4 border-white"
                        >
                            <img
                                src={product.imageUrl}
                                alt={product.name}
                                className="w-full h-[200px] rounded-lg object-cover mb-6"
                            />
                            <h1 className="text-xl font-bold font-charm mb-4">{product.name}</h1>
                            <button
                                onClick={() => setSelectedProduct(product)}
                                className="text-white px-6 py-3 border-white border-2 rounded-lg hover:bg-gray-600 transition duration-300"
                            >
                                VIEW PRODUCT
                            </button>
                        </div>
                    ))}
                </div>
            </div>

            <div className="flex justify-center gap-2 mt-3">
                <button
                    onClick={scrollLeft}
                    className="p-1 bg-gray-200 rounded-full hover:bg-gray-300 hover:scale-110 transition-all duration-200"
                >
                    <img
                        src="https://img.icons8.com/?size=24&id=26146&format=png"
                        alt="Scroll Left"
                        className="w-10 h-10"
                    />
                </button>
                <button
                    onClick={scrollRight}
                    className="p-1 bg-gray-200 rounded-full hover:bg-gray-300 hover:scale-110 transition-all duration-200"
                >
                    <img
                        src="https://img.icons8.com/?size=24&id=26147&format=png"
                        alt="Scroll Right"
                        className="w-10 h-10"
                    />
                </button>
            </div>

            {selectedProduct && (
                <div className="fixed inset-0 bg-black bg-opacity-70 flex justify-center items-center">
                    <div className="bg-black p-6 rounded-lg shadow-lg w-[90%] max-w-4xl flex">
                        <div className="w-[50%] flex justify-center items-center">
                            <img
                                src={selectedProduct.imageUrl}
                                alt={selectedProduct.name}
                                className="w-full h-full object-cover rounded-lg"
                            />
                        </div>

                        <div className="w-[50%] flex flex-col justify-between pl-6">
                            <h1 className="text-2xl font-bold mb-4 text-white">{selectedProduct.name}</h1>
                            <form className="space-y-4">
                                <div>
                                    <label className="block text-white mb-1">User Name</label>
                                    <input
                                        type="text"
                                        value={userName}
                                        onChange={(e) => setUserName(e.target.value)}
                                        className="w-full text-black border-gray-300 rounded p-2"
                                    />
                                </div>
                                <div>
                                    <label className="block text-white mb-1">Phone</label>
                                    <input
                                        type="text"
                                        value={phone}
                                        onChange={(e) => setPhone(e.target.value)}
                                        className="w-full text-black border-gray-300 rounded p-2"
                                    />
                                </div>
                                <div>
                                    <label className="block text-white mb-1">Size</label>
                                    <select
                                        value={size}
                                        onChange={(e) => setSize(e.target.value)}
                                        className="w-full text-black border-gray-300 rounded p-2"
                                    >
                                        <option value="5ml">5ml</option>
                                        <option value="10ml">10ml</option>
                                    </select>
                                </div>
                                <div>
                                    <label className="block text-white mb-1">Quantity</label>
                                    <input
                                        type="number"
                                        value={quantity}
                                        onChange={(e) => setQuantity(Number(e.target.value))}
                                        className="w-full text-black border-gray-300 rounded p-2"
                                    />
                                </div>
                                <button
                                    type="button"
                                    onClick={addToCart}
                                    className="w-full bg-gray-800 text-white py-2 rounded hover:bg-gray-900"
                                >
                                    Add to Cart
                                </button>
                                <button
                                    type="button"
                                    onClick={() => setSelectedProduct(null)}
                                    className="w-full bg-red-500 text-white py-2 rounded hover:bg-red-600 mt-2"
                                >
                                    Close
                                </button>
                            </form>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Products;
