import React, { useState, useEffect } from 'react';
import { db } from './firebase'; // Ensure you have the correct path to your firebase configuration
import { collection, getDocs, deleteDoc, doc } from 'firebase/firestore';

const Admin = () => {
  const [orders, setOrders] = useState([]);
  const [imageLinks, setImageLinks] = useState([]);
  const [titles, setTitles] = useState([]);
  const [heading, setHeading] = useState('');
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [imageUrl, setImageUrl] = useState('');
  const [loading, setLoading] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [showImageForm, setShowImageForm] = useState(false);

  // Fetching orders, image links, and products
  useEffect(() => {
    const fetchOrders = async () => {
      const ordersCollection = collection(db, 'orders');
      const orderSnapshot = await getDocs(ordersCollection);
      const orderList = orderSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setOrders(orderList);
    };

    const fetchImageLinks = async () => {
      const imageLinksCollection = collection(db, 'imageLinks');
      const snapshot = await getDocs(imageLinksCollection);
      const links = snapshot.docs.map(doc => doc.data().link);
      const titles = snapshot.docs.map(doc => doc.data().title);
      setImageLinks(links);
      setTitles(titles);
    };

    fetchOrders();
    fetchImageLinks();
  }, []);

  // Handle image link change
  const handleImageLinkChange = (e, idx) => {
    const updatedLinks = [...imageLinks];
    updatedLinks[idx] = e.target.value;
    setImageLinks(updatedLinks);
  };

  // Handle title change
  const handleTitleChange = (e, idx) => {
    const updatedTitles = [...titles];
    updatedTitles[idx] = e.target.value;
    setTitles(updatedTitles);
  };

  const handleSubmitImages = async (e) => {
    e.preventDefault();
    setLoading(true);
    // Your code for submitting images
    setLoading(false);
  };

  const handleSubmitProduct = async (e) => {
    e.preventDefault();
    setLoading(true);
    // Your code for submitting product
    setLoading(false);
  };

  const handleDelete = async (collectionName, id, setState, currentState) => {
    await deleteDoc(doc(db, collectionName, id));
    setState(currentState.filter(item => item.id !== id));
  };

  return (
    <div>
      <div>
        {/* Show Orders */}
        {orders.length > 0 && (
          <div className="mb-6">
            <h2 className="text-2xl text-center font-semibold mb-4">Orders</h2>
            <ul>
              {orders.map(order => (
                <li key={order.id} className="mb-4">
                  <p>{order.name}</p>
                  <p>{new Date(order.timestamp.seconds * 1000).toLocaleString()}</p>
                  <button
                    className="mt-4 bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600 transition"
                    onClick={() => handleDelete('orders', order.id, setOrders, orders)}
                  >
                    Delete Order
                  </button>
                </li>
              ))}
            </ul>
          </div>
        )}

        {/* Show Image Links */}
        {showImageForm && (
          <div className="mb-6">
            <h2 className="text-2xl text-center font-semibold mb-4">Add News</h2>
            <form onSubmit={handleSubmitImages} className="flex flex-col gap-4">
              <div className="flex gap-4">
                {imageLinks.map((link, idx) => (
                  <div key={idx} className="w-1/3">
                    <input
                      type="text"
                      placeholder={`Image URL ${idx + 1}`}
                      value={link}
                      onChange={(e) => handleImageLinkChange(e, idx)}
                      className="w-full px-4 py-2 rounded-md border border-gray-300 focus:outline-none"
                    />
                    <input
                      type="text"
                      placeholder={`Title ${idx + 1}`}
                      value={titles[idx]}
                      onChange={(e) => handleTitleChange(e, idx)}
                      className="w-full mt-2 px-4 py-2 rounded-md border border-gray-300 focus:outline-none"
                    />
                  </div>
                ))}
              </div>
              <input
                type="text"
                placeholder="Heading"
                value={heading}
                onChange={(e) => setHeading(e.target.value)}
                className="w-full px-4 py-2 rounded-md border border-gray-300 focus:outline-none"
              />
              <button
                type="submit"
                className="bg-blue-500 text-white px-6 py-2 rounded-md hover:bg-blue-600 transition"
                disabled={loading}
              >
                {loading ? "Submitting..." : "Submit Images"}
              </button>
            </form>
          </div>
        )}

        {/* Show Product Form */}
        {showForm && (
          <div className="mb-6">
            <h2 className="text-2xl text-center font-semibold mb-4">Add Product</h2>
            <form onSubmit={handleSubmitProduct} className="flex flex-col gap-4">
              <input
                type="text"
                placeholder="Product Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                className="px-4 py-2 rounded-md border border-gray-300 focus:outline-none"
              />
              <input
                type="text"
                placeholder="Product Description"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                className="px-4 py-2 rounded-md border border-gray-300 focus:outline-none"
              />
              <input
                type="text"
                placeholder="Price"
                value={price}
                onChange={(e) => setPrice(e.target.value)}
                className="px-4 py-2 rounded-md border border-gray-300 focus:outline-none"
              />
              <input
                type="text"
                placeholder="Product Image URL"
                value={imageUrl}
                onChange={(e) => setImageUrl(e.target.value)}
                className="px-4 py-2 rounded-md border border-gray-300 focus:outline-none"
              />
              <button
                type="submit"
                className="bg-blue-500 text-white px-6 py-2 rounded-md hover:bg-blue-600 transition"
                disabled={loading}
              >
                {loading ? "Submitting..." : "Add Product"}
              </button>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default Admin;
