import React, { useState, useEffect } from "react";
import { addDoc, collection, getDocs, deleteDoc, doc } from "firebase/firestore";
import { db } from "../../firebase/connection";

const Admin = () => {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [loading, setLoading] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [products, setProducts] = useState([]);

  // Fetch products from Firestore
  useEffect(() => {
    if (showForm) {
      const fetchProducts = async () => {
        try {
          setLoading(true);
          const productsCollection = collection(db, "products");
          const productSnapshot = await getDocs(productsCollection);
          const productList = productSnapshot.docs.map((doc) => ({ ...doc.data(), id: doc.id }));
          setProducts(productList);
        } catch (error) {
          console.error("Error fetching products:", error);
          alert("Failed to fetch products.");
        } finally {
          setLoading(false);
        }
      };

      fetchProducts();
    }
  }, [showForm]);

  // Handle adding a product
  const handleSubmitProduct = async (e) => {
    e.preventDefault();

    if (!name || !description || !price || !imageUrl) {
      alert("Please fill in all fields.");
      return;
    }

    setLoading(true);
    try {
      const productsCollection = collection(db, "products");
      await addDoc(productsCollection, { name, description, price, imageUrl });
      alert("Product added successfully!");
      setName("");
      setDescription("");
      setPrice("");
      setImageUrl("");
      setShowForm(false);
    } catch (error) {
      console.error("Error adding product:", error.message);
      alert("Failed to add product.");
    } finally {
      setLoading(false);
    }
  };

  // Product Form Component
  const ProductForm = ({ onSubmit, loading, setName, setDescription, setPrice, setImageUrl, name, description, price, imageUrl }) => (
    <div className="mb-6">
      <h2 className="text-2xl text-center font-semibold mb-4">Add New Product</h2>
      <form onSubmit={onSubmit} className="space-y-4">
        <div>
          <label htmlFor="name" className="block text-lg font-medium mb-2">Product Name</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded-md"
            placeholder="Enter product name"
            required
          />
        </div>
        <div>
          <label htmlFor="description" className="block text-lg font-medium mb-2">Product Description</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded-md"
            placeholder="Enter product description"
            required
          />
        </div>
        <div>
          <label htmlFor="price" className="block text-lg font-medium mb-2">Price</label>
          <input
            type="number"
            id="price"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded-md"
            placeholder="Enter product price"
            required
          />
        </div>
        <div>
          <label htmlFor="imageUrl" className="block text-lg font-medium mb-2">Image URL</label>
          <input
            type="text"
            id="imageUrl"
            value={imageUrl}
            onChange={(e) => setImageUrl(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded-md"
            placeholder="Enter product image URL"
            required
          />
        </div>
        <div className="text-center">
          <button
            type="submit"
            className="bg-blue-500 text-white px-6 py-2 rounded-md hover:bg-blue-600 transition"
            disabled={loading}
          >
            {loading ? "Adding..." : "Add Product"}
          </button>
        </div>
      </form>
    </div>
  );

  return (
    <div className="flex justify-center p-5">
      <div className="w-full">
        <h1 className="text-3xl font-bold text-center mb-6">Admin Panel</h1>

        {/* Toggle buttons */}
        <div className="flex gap-4 mb-6 justify-center">
          <button
            className="bg-blue-500 text-white px-6 py-2 rounded-md hover:bg-blue-600 transition"
            onClick={() => setShowForm(!showForm)}
          >
            {showForm ? "Hide Products" : "See Products"}
          </button>
        </div>

        {/* Show Products */}
        {showForm && (
          <>
            <div className="mb-6">
              <h2 className="text-2xl text-center font-semibold mb-4">All Products</h2>
              <ul className="flex flex-wrap gap-4 justify-center">
                {products.length === 0 ? (
                  <p>No products available.</p>
                ) : (
                  products.map((product) => (
                    <li key={product.id} className="bg-gray-100 p-4 rounded-lg mb-4 w-full sm:w-1/2 md:w-1/3 lg:w-1/4">
                      <img
                        src={product.imageUrl}
                        alt={product.name}
                        className="w-full h-48 object-cover mb-4 rounded-md"
                      />
                      <div className="flex flex-col">
                        <h3 className="font-bold text-lg">{product.name}</h3>
                        <p className="text-gray-700">{product.description}</p>
                        <p className="text-gray-500">{`Price: $${product.price}`}</p>
                      </div>
                    </li>
                  ))
                )}
              </ul>
            </div>

            <ProductForm 
              onSubmit={handleSubmitProduct}
              loading={loading}
              setName={setName}
              setDescription={setDescription}
              setPrice={setPrice}
              setImageUrl={setImageUrl}
              name={name}
              description={description}
              price={price}
              imageUrl={imageUrl}
            />
          </>
        )}
      </div>
    </div>
  );
};

export default Admin;
