import React, { useState, useEffect } from "react";
import { db } from "../firebaseConfig"; // Import Firebase configuration
import {
  collection,
  getDocs,
  addDoc,
  deleteDoc,
  doc,
} from "firebase/firestore";

const Admin = () => {
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [news, setNews] = useState([]);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [loading, setLoading] = useState(false);

  // Fetch data from Firestore
  useEffect(() => {
    const fetchData = async () => {
      try {
        const productSnapshot = await getDocs(collection(db, "products"));
        const orderSnapshot = await getDocs(collection(db, "orders"));
        const newsSnapshot = await getDocs(collection(db, "news"));

        setProducts(
          productSnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }))
        );
        setOrders(orderSnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() })));
        setNews(newsSnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() })));
      } catch (error) {
        console.error("Error fetching data: ", error.message);
      }
    };

    fetchData();
  }, []);

  // Add Product
  const addProduct = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      await addDoc(collection(db, "products"), {
        name,
        description,
        price: parseFloat(price),
        imageUrl,
      });

      setProducts([
        ...products,
        { name, description, price: parseFloat(price), imageUrl },
      ]);
      setName("");
      setDescription("");
      setPrice("");
      setImageUrl("");
      alert("Product added successfully!");
    } catch (error) {
      console.error("Error adding product: ", error.message);
      alert("Failed to add product.");
    } finally {
      setLoading(false);
    }
  };

  // Delete Item (Reusable Function)
  const deleteItem = async (collectionName, id, stateUpdater, state) => {
    try {
      const docRef = doc(db, collectionName, id);
      await deleteDoc(docRef);
      alert(`${collectionName} item deleted successfully!`);
      stateUpdater(state.filter((item) => item.id !== id));
    } catch (error) {
      console.error(`Error deleting ${collectionName} item:`, error.message);
      alert(`Failed to delete ${collectionName} item.`);
    }
  };

  return (
    <div className="p-8 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Admin Panel</h1>

      {/* Add Product Form */}
      <div className="mb-8">
        <h2 className="text-xl font-semibold mb-4">Add Product</h2>
        <form onSubmit={addProduct} className="border p-4 rounded-md shadow">
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Product Name"
            className="w-full p-2 mb-4 border rounded-md focus:outline-none focus:ring focus:ring-blue-300"
          />
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Product Description"
            className="w-full p-2 mb-4 border rounded-md focus:outline-none focus:ring focus:ring-blue-300"
          />
          <input
            type="number"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            placeholder="Product Price"
            className="w-full p-2 mb-4 border rounded-md focus:outline-none focus:ring focus:ring-blue-300"
          />
          <input
            type="text"
            value={imageUrl}
            onChange={(e) => setImageUrl(e.target.value)}
            placeholder="Image URL"
            className="w-full p-2 mb-4 border rounded-md focus:outline-none focus:ring focus:ring-blue-300"
          />
          <button
            type="submit"
            disabled={loading}
            className={`w-full p-2 text-white rounded-md ${
              loading ? "bg-gray-500 cursor-not-allowed" : "bg-blue-500 hover:bg-blue-600"
            }`}
          >
            {loading ? "Adding Product..." : "Add Product"}
          </button>
        </form>
      </div>

      {/* Products Section */}
      <div className="mb-8">
        <h2 className="text-xl font-semibold mb-4">Products</h2>
        <ul>
          {products.map((product) => (
            <li key={product.id} className="flex justify-between items-center p-2 border-b">
              <span>{product.name} - ${product.price}</span>
              <button
                onClick={() =>
                  deleteItem("products", product.id, setProducts, products)
                }
                className="px-2 py-1 text-white bg-red-500 rounded-md hover:bg-red-600"
              >
                Delete
              </button>
            </li>
          ))}
        </ul>
      </div>

      {/* Orders Section */}
      <div className="mb-8">
        <h2 className="text-xl font-semibold mb-4">Orders</h2>
        <ul>
          {orders.map((order) => (
            <li key={order.id} className="flex justify-between items-center p-2 border-b">
              <span>{order.customerName} - ${order.totalAmount}</span>
              <button
                onClick={() => deleteItem("orders", order.id, setOrders, orders)}
                className="px-2 py-1 text-white bg-red-500 rounded-md hover:bg-red-600"
              >
                Delete
              </button>
            </li>
          ))}
        </ul>
      </div>

      {/* News Section */}
      <div className="mb-8">
        <h2 className="text-xl font-semibold mb-4">News</h2>
        <ul>
          {news.map((newsItem) => (
            <li key={newsItem.id} className="flex justify-between items-center p-2 border-b">
              <span>{newsItem.title}</span>
              <button
                onClick={() => deleteItem("news", newsItem.id, setNews, news)}
                className="px-2 py-1 text-white bg-red-500 rounded-md hover:bg-red-600"
              >
                Delete
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Admin;
