import React, { useState, useEffect } from "react";
import { addDoc, collection, getDocs, deleteDoc, doc } from "firebase/firestore";
import { db } from "../../firebase/connection";

const Admin = () => {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [heading, setHeading] = useState("");
  const [loading, setLoading] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [showImageForm, setShowImageForm] = useState(false);
  const [imageLinks, setImageLinks] = useState(["", "", ""]);
  const [titles, setTitles] = useState(["", "", ""]);
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);

  // Fetch products and orders
  useEffect(() => {
    if (showForm) {
      const fetchProducts = async () => {
        const productsCollection = collection(db, "products");
        const productSnapshot = await getDocs(productsCollection);
        const productList = productSnapshot.docs.map((doc) => ({ ...doc.data(), id: doc.id }));
        setProducts(productList);
      };
      fetchProducts();
    }

    if (orders.length === 0) {
      const fetchOrders = async () => {
        const ordersCollection = collection(db, "orders");
        const orderSnapshot = await getDocs(ordersCollection);
        const orderList = orderSnapshot.docs.map((doc) => ({ ...doc.data(), id: doc.id }));
        setOrders(orderList);
      };
      fetchOrders();
    }
  }, [showForm, orders.length]);

  // Handle add product
  const handleSubmitProduct = async (e) => {
    e.preventDefault();
    if (!name || !description || !price || !imageUrl) {
      alert("Please fill in all fields.");
      return;
    }

    setLoading(true);
    try {
      const productsCollection = collection(db, "products");
      await addDoc(productsCollection, { name, description, price, imageUrl });
      alert("Product added successfully!");
      resetForm();
    } catch (error) {
      console.error("Error adding product:", error);
      alert("Failed to add product.");
    } finally {
      setLoading(false);
    }
  };

  // Reset product form
  const resetForm = () => {
    setName("");
    setDescription("");
    setPrice("");
    setImageUrl("");
    setShowForm(false);
  };

  // Handle image link and title changes dynamically
  const handleImageLinkChange = (e, idx) => {
    const updatedLinks = [...imageLinks];
    updatedLinks[idx] = e.target.value;
    setImageLinks(updatedLinks);
  };

  const handleTitleChange = (e, idx) => {
    const updatedTitles = [...titles];
    updatedTitles[idx] = e.target.value;
    setTitles(updatedTitles);
  };

  // Handle adding images
  const handleSubmitImages = async (e) => {
    e.preventDefault();

    if (imageLinks.some((link) => !link) || titles.some((title) => !title) || !heading) {
      alert("Please fill in all image links, titles, and heading.");
      return;
    }

    setLoading(true);
    try {
      const adsCollection = collection(db, "headads");
      for (let i = 0; i < imageLinks.length; i++) {
        await addDoc(adsCollection, { imageUrl: imageLinks[i], title: titles[i], heading });
      }
      alert("Images added successfully!");
      setImageLinks(["", "", ""]);
      setTitles(["", "", ""]);
      setHeading("");
    } catch (error) {
      console.error("Error adding images:", error);
      alert("Failed to add images.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex justify-center min-h-screen p-5">
      <div className="w-full max-w-4xl">
        <h1 className="text-3xl font-bold text-center mb-6">Admin Panel</h1>

        {/* Toggle buttons */}
        <div className="flex gap-4 mb-6 justify-center">
          <button onClick={() => setShowForm(!showForm)}>{showForm ? "Hide Products" : "See Products"}</button>
          <button onClick={() => setShowImageForm(!showImageForm)}>{showImageForm ? "Hide News Form" : "Add News"}</button>
        </div>

        {/* Show Products */}
        {showForm && (
          <div className="mb-6">
            <h2 className="text-2xl text-center font-semibold mb-4">All Products</h2>
            <ul className="flex flex-wrap gap-4 justify-center">
              {products.length === 0 ? (
                <p>No products available.</p>
              ) : (
                products.map((product) => (
                  <li key={product.id}>
                    <img src={product.imageUrl} alt={product.name} />
                    <p>{product.name}</p>
                    <button onClick={() => handleDeleteProduct(product.id)}>Delete</button>
                  </li>
                ))
              )}
            </ul>
          </div>
        )}

        {/* Show Orders */}
        {orders.length > 0 && (
          <div className="mb-6">
            <h2 className="text-2xl text-center font-semibold mb-4">All Orders</h2>
            {orders.map((order) => (
              <div key={order.id}>
                <p>{order.userName}</p>
                <button onClick={() => handleDeleteOrder(order.id)}>Delete</button>
              </div>
            ))}
          </div>
        )}

        {/* Product Form */}
        {showForm && (
          <form onSubmit={handleSubmitProduct}>
            <input value={name} onChange={(e) => setName(e.target.value)} />
            <textarea value={description} onChange={(e) => setDescription(e.target.value)} />
            <input value={price} onChange={(e) => setPrice(e.target.value)} />
            <input value={imageUrl} onChange={(e) => setImageUrl(e.target.value)} />
            <button type="submit">Add Product</button>
          </form>
        )}
      </div>
    </div>
  );
};

export default Admin;
