import React, { useState } from "react";

const Footer = () => {
    const [content, setContent] = useState("");

    const handleShowTerms = () => {
        setContent(
            `Conditions d'Utilisation\n\n
            Bienvenue sur notre site web. En accédant à ce site, vous acceptez les présentes conditions générales. 
            Si vous n'êtes pas d'accord avec ces conditions, veuillez ne pas utiliser notre site.\n\n
            Utilisation du Site\n
            Vous devez avoir au moins 18 ans ou être sous la supervision d’un parent/tuteur légal pour utiliser ce site. 
            L’utilisation commerciale est interdite sans notre autorisation expresse. Toute violation de ces conditions 
            peut entraîner la suspension de votre accès.\n\n
            Contenu et Propriété\n
            Tous les contenus présents sur le site, y compris textes, images et graphiques, sont protégés par les droits d'auteur 
            et sont notre propriété exclusive.\n\n
            Responsabilité de l'Utilisateur\n
            Vous êtes responsable de vos informations de connexion et devez signaler toute utilisation non autorisée de votre compte. 
            Nous ne sommes pas responsables des pertes liées à la négligence de ces informations.\n\n
            Prix et Commandes\n
            Nous nous efforçons d’assurer l'exactitude des prix, mais nous nous réservons le droit d'annuler une commande en cas d'erreur 
            ou de demande incomplète.\n\n
            Modifications des Conditions\n
            Ces conditions peuvent être modifiées à tout moment. En continuant à utiliser notre site après des changements, 
            vous acceptez ces modifications.\n\n
            Si vous avez des questions, veuillez nous contacter.`
        );
    };

    const handleShowContact = () => {
        setContent("Contactez-nous au : 999");
    };

    return (
        <footer id="Contact" className="relative py-8 flex flex-col border-t-2 border-gray-700 space-y-10">
            <div className="flex flex-col items-center">
                <a href="#news">
                    <img src="/name.png" alt="logo" width="300px" className="mb-10" />
                </a>
            </div>
            <div className="flex justify-around">
                <div className="p-10 flex flex-col justify-between gap-2">
                    <h1 className="text-xl font-bold">Terms et Politiques</h1>
                    <button 
                        onClick={handleShowTerms} 
                        className="text-base text-gray-500 hover:text-white"
                    >
                        Condition d'utilisation
                    </button>
                </div>
                <div className="p-10 flex flex-col justify-between gap-2">
                    <h1 className="text-xl font-bold">À propos</h1>
                    <button 
                        onClick={handleShowContact} 
                        className="text-base text-gray-500 hover:text-white"
                    >
                        Contact
                    </button>
                </div>
            </div>
            <div className="bg-gray-800 text-white p-6 rounded-md mt-6">
                <p className="whitespace-pre-line">{content}</p>
            </div>
        </footer>
    );
};

export default Footer;
