import React, { useState, useRef, useEffect } from "react";
import { collection, getDocs, addDoc } from "firebase/firestore";
import { db } from "../../firebase/connection";

const Products = () => {
    const [products, setProducts] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [quantity, setQuantity] = useState(1);
    const [size, setSize] = useState("5ml");
    const [phone, setPhone] = useState("");
    const [userName, setUserName] = useState("");
    const [address, setAddress] = useState("");
    const scrollerRef = useRef(null);
    const [cart, setCart] = useState(() => {
        // Load cart from localStorage on initial load
        const savedCart = localStorage.getItem("cart");
        return savedCart ? JSON.parse(savedCart) : [];
    });
    const [cartModalOpen, setCartModalOpen] = useState(false);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const querySnapshot = await getDocs(collection(db, "products"));
                const productsData = querySnapshot.docs.map((doc) => ({
                    id: doc.id,
                    ...doc.data(),
                }));
                setProducts(productsData);
                setFilteredProducts(productsData);
            } catch (error) {
                console.error("Error fetching products: ", error);
            }
        };

        fetchProducts();
    }, []);

    useEffect(() => {
        // Save cart to localStorage whenever it changes
        localStorage.setItem("cart", JSON.stringify(cart));
    }, [cart]);

    const handleSearchChange = (e) => {
        const value = e.target.value;
        setSearchTerm(value);

        if (value) {
            const filteredData = products.filter((product) =>
                product.name.toLowerCase().includes(value.toLowerCase())
            );
            setFilteredProducts(filteredData);
        } else {
            setFilteredProducts(products);
        }
    };

    const addToCart = () => {
        if (!selectedProduct) return;

        const newCartItem = {
            ...selectedProduct,
            size,
            quantity,
        };

        setCart((prevCart) => [...prevCart, newCartItem]);
        setSelectedProduct(null); // Close modal
    };

    const scrollLeft = () => {
        if (scrollerRef.current) {
            scrollerRef.current.scrollBy({ left: -200, behavior: "smooth" });
        }
    };

    const scrollRight = () => {
        if (scrollerRef.current) {
            scrollerRef.current.scrollBy({ left: 200, behavior: "smooth" });
        }
    };

    const handleCartSubmit = async () => {
        if (!userName.trim() || !phone.trim() || !address.trim()) {
            alert("Please fill in all the required fields.");
            return;
        }

        try {
            await Promise.all(
                cart.map((item) =>
                    addDoc(collection(db, "orders"), {
                        userName: userName.trim(),
                        phone: phone.trim(),
                        address: address.trim(),
                        productName: item.name,
                        size: item.size,
                        quantity: item.quantity,
                        timestamp: new Date().toISOString(),
                    })
                )
            );
            alert("Order placed successfully!");
            setCart([]); // Clear cart
            setCartModalOpen(false); // Close modal
            setUserName("");
            setPhone("");
            setAddress("");
        } catch (error) {
            console.error("Error placing order: ", error);
            alert("Failed to place the order. Please try again later.");
        }
    };

    return (
        <div id="products" className="pt-5 px-4">
            <button
                onClick={() => setCartModalOpen(true)}
                className="fixed bottom-10 right-1/2 translate-x-1/2 bg-gray-800 text-white w-[80px] h-[80px] rounded-full shadow-lg border-2 border-dashed border-white hover:bg-gray-700 transition duration-300 z-50"
            >
                Cart
            </button>

            {/* Products and Search */}
            <div className="flex w-[80%] mx-auto pt-5">
                <input
                    type="text"
                    value={searchTerm}
                    onChange={handleSearchChange}
                    className="flex-grow border-2 border-gray-300 rounded-l-md p-2 text-black focus:outline-none"
                    placeholder="Search for a product..."
                />
            </div>

            {/* Product List */}
            <div ref={scrollerRef} className="flex overflow-x-auto gap-6 py-4 scrollbar-hide">
                {filteredProducts.map((product) => (
                    <div key={product.id} className="min-w-[300px]">
                        <h1>{product.name}</h1>
                        <button onClick={() => setSelectedProduct(product)}>View</button>
                    </div>
                ))}
            </div>

            {/* Selected Product Modal */}
            {selectedProduct && (
                <div
                    onClick={(e) => {
                        if (e.target === e.currentTarget) setSelectedProduct(null);
                    }}
                    className="fixed inset-0 flex justify-center items-center bg-black bg-opacity-70"
                >
                    <div className="bg-white p-6 rounded-lg">
                        <h1>{selectedProduct.name}</h1>
                        <button onClick={addToCart}>Add to Cart</button>
                        <button onClick={() => setSelectedProduct(null)}>Close</button>
                    </div>
                </div>
            )}

            {/* Cart Modal */}
            {cartModalOpen && (
                <div
                    onClick={(e) => {
                        if (e.target === e.currentTarget) setCartModalOpen(false);
                    }}
                    className="fixed inset-0 flex justify-center items-center bg-black bg-opacity-70"
                >
                    <div className="bg-white w-[90%] max-w-4xl p-6 rounded-lg flex">
                        <div className="w-1/2">
                            <h2>Cart Items</h2>
                            {cart.map((item, index) => (
                                <div key={index}>
                                    <h3>{item.name}</h3>
                                    <p>Size: {item.size}</p>
                                    <p>Quantity: {item.quantity}</p>
                                </div>
                            ))}
                        </div>
                        <div className="w-1/2">
                            <h2>User Details</h2>
                            <input
                                type="text"
                                value={userName}
                                onChange={(e) => setUserName(e.target.value)}
                                placeholder="Your Name"
                            />
                            <input
                                type="text"
                                value={phone}
                                onChange={(e) => setPhone(e.target.value)}
                                placeholder="Phone"
                            />
                            <input
                                type="text"
                                value={address}
                                onChange={(e) => setAddress(e.target.value)}
                                placeholder="Address"
                            />
                            <button onClick={handleCartSubmit}>Submit</button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Products;
