import React, { useState } from "react";

const Admin = () => {
  const [news, setNews] = useState([]);
  const [heading, setHeading] = useState("");
  const [imageLinks, setImageLinks] = useState(["", "", ""]);
  const [titles, setTitles] = useState(["", "", ""]);
  const [loading, setLoading] = useState(false);

  const handleImageLinkChange = (e, index) => {
    const newImageLinks = [...imageLinks];
    newImageLinks[index] = e.target.value;
    setImageLinks(newImageLinks);
  };

  const handleTitleChange = (e, index) => {
    const newTitles = [...titles];
    newTitles[index] = e.target.value;
    setTitles(newTitles);
  };

  const handleSubmitImages = (e) => {
    e.preventDefault();

    // Create a new news object
    const newNews = imageLinks.map((link, index) => ({
      imageUrl: link,
      title: titles[index],
      heading: heading,
    }));

    // Add the new news items to the state
    setNews((prevNews) => [...prevNews, ...newNews]);

    // Reset the form
    setHeading("");
    setImageLinks(["", "", ""]);
    setTitles(["", "", ""]);
    setLoading(false);
  };

  const handleDeleteNews = (index) => {
    const updatedNews = news.filter((_, i) => i !== index);
    setNews(updatedNews);
  };

  return (
    <div className="admin-container">
      <div className="max-w-4xl mx-auto p-4">
        {/* Add News Form */}
        {news.length === 0 && (
          <form className="mb-6" onSubmit={handleSubmitImages}>
            <div className="flex gap-4 flex-wrap justify-center mb-4">
              <div className="w-1/3">
                <label htmlFor="heading" className="block text-sm font-medium">
                  Heading
                </label>
                <input
                  type="text"
                  id="heading"
                  value={heading}
                  onChange={(e) => setHeading(e.target.value)}
                  className="mt-1 block w-full p-2 border rounded-md"
                />
              </div>
            </div>

            {[0, 1, 2].map((idx) => (
              <div key={idx} className="flex gap-4 flex-wrap justify-center mb-4">
                <div className="w-1/3">
                  <label htmlFor={`imageLink${idx}`} className="block text-sm font-medium">
                    Image URL {idx + 1}
                  </label>
                  <input
                    type="text"
                    id={`imageLink${idx}`}
                    value={imageLinks[idx]}
                    onChange={(e) => handleImageLinkChange(e, idx)}
                    className="mt-1 block w-full p-2 border rounded-md"
                  />
                </div>
                <div className="w-1/3">
                  <label htmlFor={`title${idx}`} className="block text-sm font-medium">
                    Title {idx + 1}
                  </label>
                  <input
                    type="text"
                    id={`title${idx}`}
                    value={titles[idx]}
                    onChange={(e) => handleTitleChange(e, idx)}
                    className="mt-1 block w-full p-2 border rounded-md"
                  />
                </div>
              </div>
            ))}

            <button
              type="submit"
              className="bg-blue-500 text-white px-6 py-2 rounded-md hover:bg-blue-600 transition"
            >
              {loading ? "Loading..." : "Add News"}
            </button>
          </form>
        )}

        {/* Display News */}
        {news.length > 0 && (
          <div className="mt-8">
            <h2 className="text-2xl font-semibold text-center mb-4">News</h2>
            <ul className="flex flex-wrap gap-4 justify-center">
              {news.map((newsItem, idx) => (
                <li key={idx} className="bg-white p-4 rounded-lg mb-4 w-full sm:w-1/2 md:w-1/3 lg:w-1/4">
                  <img
                    src={newsItem.imageUrl}
                    alt={newsItem.title}
                    className="w-full h-48 object-cover mb-4 rounded-md"
                  />
                  <div className="flex flex-col">
                    <h3 className="font-bold text-lg">{newsItem.title}</h3>
                    <p className="text-gray-700">{newsItem.heading}</p>
                    <button
                      className="mt-4 bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600 transition"
                      onClick={() => handleDeleteNews(idx)}
                    >
                      Delete News
                    </button>
                  </div>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default Admin;
