import React, { useState, useEffect } from "react";
import { addDoc, collection, getDocs } from "firebase/firestore";
import { db } from "../../firebase/connection";

const Admin = () => {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [heading, setHeading] = useState("");
  const [loading, setLoading] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [showImageForm, setShowImageForm] = useState(false);
  const [imageLinks, setImageLinks] = useState(["", "", ""]);
  const [titles, setTitles] = useState(["", "", ""]);
  const [products, setProducts] = useState([]);  // Store all products

  // Fetch products from Firestore
  useEffect(() => {
    const fetchProducts = async () => {
      const productsCollection = collection(db, "products");
      const productSnapshot = await getDocs(productsCollection);
      const productList = productSnapshot.docs.map((doc) => doc.data());
      setProducts(productList);
    };

    if (showForm) {
      fetchProducts();  // Fetch products when the form is visible
    }
  }, [showForm]);

  // Handle adding product
  const handleSubmitProduct = async (e) => {
    e.preventDefault();

    if (!name || !description || !price || !imageUrl) {
      alert("Please fill in all fields.");
      return;
    }

    setLoading(true);
    try {
      const productsCollection = collection(db, "products");
      await addDoc(productsCollection, { name, description, price, imageUrl });
      alert("Product added successfully!");
      setName("");
      setDescription("");
      setPrice("");
      setImageUrl("");
      setShowForm(false);  // Hide the form after successful submission
    } catch (error) {
      console.error("Error adding product:", error.message);
      alert("Failed to add product.");
    } finally {
      setLoading(false);
    }
  };

  // Handle adding images
  const handleSubmitImages = async (e) => {
    e.preventDefault();

    if (imageLinks.some((link) => !link) || titles.some((title) => !title) || !heading) {
      alert("Please fill in all image links, titles, and heading.");
      return;
    }

    setLoading(true);
    try {
      const adsCollection = collection(db, "headads");

      for (let i = 0; i < imageLinks.length; i++) {
        await addDoc(adsCollection, {
          imageUrl: imageLinks[i],
          title: titles[i],
          heading,
        });
      }

      alert("Images added successfully!");
      setImageLinks(["", "", ""]);
      setTitles(["", "", ""]);
      setHeading("");
    } catch (error) {
      console.error("Error adding images:", error.message);
      alert("Failed to add images.");
    } finally {
      setLoading(false);
    }
  };

  // Handle Image link and title changes dynamically
  const handleImageLinkChange = (e, idx) => {
    const updatedLinks = [...imageLinks];
    updatedLinks[idx] = e.target.value;
    setImageLinks(updatedLinks);
  };

  const handleTitleChange = (e, idx) => {
    const updatedTitles = [...titles];
    updatedTitles[idx] = e.target.value;
    setTitles(updatedTitles);
  };

  return (
    <div className="max-w-lg mx-auto p-5">
      <h1 className="text-3xl font-bold text-center mb-6">Admin Panel</h1>

      {/* Toggle buttons */}
      <div className="flex gap-4 mb-6">
        <button
          className="bg-blue-500 text-white px-6 py-2 rounded-md hover:bg-blue-600 transition"
          onClick={() => setShowForm(!showForm)}
        >
          {showForm ? "Hide Products" : "See Products"}
        </button>
        <button
          className="bg-green-500 text-white px-6 py-2 rounded-md hover:bg-green-600 transition"
          onClick={() => setShowImageForm(!showImageForm)}
        >
          {showImageForm ? "Hide News Form" : "Add News"}
        </button>
      </div>

      {/* Show Products */}
      {/* Show Products */}
      {showForm && (
        <div className="mb-6">
          <h2 className="text-2xl font-semibold mb-4">All Products</h2>
          <ul>
            {products.length === 0 ? (
              <p>No products available.</p>
            ) : (
              products.map((product, index) => (
                <li key={index} className="bg-gray-100 p-4 rounded-lg mb-4">
                  {/* Product Image */}
                  <img src={product.imageUrl} alt={product.name} className="w-full h-48 object-cover mb-4 rounded-md" />

                  {/* Product Info */}
                  <div className="flex flex-col">
                    <h3 className="font-bold text-lg">{product.name}</h3>
                    <p className="text-gray-700">{product.description}</p>
                    <p className="text-gray-500">{`Price: $${product.price}`}</p>
                  </div>
                </li>
              ))
            )}
          </ul>
        </div>
      )}

      {/* Product Form */}
      {showForm && (
        <div className="mb-6">
          <h2 className="text-2xl font-semibold mb-4">All Products</h2>
          <div className="flex flex-wrap gap-6">
            {products.length === 0 ? (
              <p>No products available.</p>
            ) : (
              products.map((product, index) => (
                <div key={index} className="w-full sm:w-1/2 md:w-1/4 p-4">
                  <div className="bg-gray-100 p-4 rounded-lg shadow-md">
                    {/* Product Image */}
                    <img src={product.imageUrl} alt={product.name} className="w-full h-48 object-cover mb-4 rounded-md" />

                    {/* Product Info */}
                    <div className="flex flex-col mb-4">
                      <h3 className="font-bold text-lg">{product.name}</h3>
                      <p className="text-gray-700">{product.description}</p>
                      <p className="text-gray-500">{`Price: $${product.price}`}</p>
                    </div>

                    {/* Delete Button */}
                    <button
                      className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600 transition"
                      onClick={() => handleDeleteProduct(product.id)}
                    >
                      Delete
                    </button>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      )}


      {/* Images Form */}
      {showImageForm && (
        <form className="bg-gray-100 p-6 rounded-lg shadow-md" onSubmit={handleSubmitImages}>
          <div className="mb-4">
            <label htmlFor="heading" className="block text-gray-700 font-medium mb-2">Heading</label>
            <input
              id="heading"
              type="text"
              value={heading}
              onChange={(e) => setHeading(e.target.value)}
              className="w-full border border-gray-300 rounded-md p-2 focus:ring-2 focus:ring-blue-500 text-black"
              required
            />
          </div>

          {imageLinks.map((_, idx) => (
            <div className="mb-4" key={idx}>
              <label htmlFor={`image-${idx}`} className="block text-gray-700 font-medium mb-2">{`Image ${idx + 1} URL`}</label>
              <input
                id={`image-${idx}`}
                type="url"
                value={imageLinks[idx]}
                onChange={(e) => handleImageLinkChange(e, idx)}
                className="w-full border border-gray-300 rounded-md p-2 focus:ring-2 focus:ring-blue-500 text-black"
                required
              />
              <label htmlFor={`title-${idx}`} className="block text-gray-700 font-medium mb-2">{`Image ${idx + 1} Title`}</label>
              <input
                id={`title-${idx}`}
                type="text"
                value={titles[idx]}
                onChange={(e) => handleTitleChange(e, idx)}
                className="w-full border border-gray-300 rounded-md p-2 focus:ring-2 focus:ring-blue-500 text-black"
                required
              />
            </div>
          ))}

          <button
            type="submit"
            className={`w-full bg-green-500 text-white py-2 rounded-md hover:bg-green-600 transition ${loading ? "opacity-50 cursor-not-allowed" : ""}`}
            disabled={loading}
          >
            {loading ? "Adding..." : "Add News"}
          </button>
        </form>
      )}
    </div>
  );
};

export default Admin;
