    return (
        <footer id="Contact" className="pt-24 mt-5 relative py-8 flex flex-col border-t-2 border-gray-700 ">
            <div>
                <a href="#news"><img src="/name.png" alt="logo" width="300px" className="pl-20" /></a>
            </div>
            <div className=" p-10 flex justify-between ">

            </div>
        </footer>
    );
}

export default Footer;
