import React, { useState, useEffect } from "react";
import { addDoc, collection, getDocs, deleteDoc, doc } from "firebase/firestore";
import { db } from "../../firebase/connection";

const Admin = () => {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [loading, setLoading] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [showImageForm, setShowImageForm] = useState(false);
  const [imageLinks, setImageLinks] = useState(["", "", ""]);
  const [titles, setTitles] = useState(["", "", ""]);
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);

  // Fetch products from Firestore
  useEffect(() => {
    if (showForm) {
      const fetchProducts = async () => {
        const productsCollection = collection(db, "products");
        const productSnapshot = await getDocs(productsCollection);
        const productList = productSnapshot.docs.map((doc) => ({ ...doc.data(), id: doc.id }));
        setProducts(productList);
      };

      fetchProducts();
    }
  }, [showForm]);

  // Fetch orders once when the component mounts
  useEffect(() => {
    const fetchOrders = async () => {
      const ordersCollection = collection(db, "orders");
      const orderSnapshot = await getDocs(ordersCollection);
      const orderList = orderSnapshot.docs.map((doc) => ({
        ...doc.data(),
        id: doc.id
      }));
      setOrders(orderList);
    };

    if (orders.length === 0) {
      fetchOrders();
    }
  }, []);  // Empty dependency array ensures it only runs once

  const handleSubmitProduct = async (e) => {
    e.preventDefault();

    if (!name || !description || !price || !imageUrl) {
      alert("Please fill in all fields.");
      return;
    }

    setLoading(true);
    try {
      const productsCollection = collection(db, "products");
      await addDoc(productsCollection, { name, description, price, imageUrl });
      alert("Product added successfully!");
      setName("");
      setDescription("");
      setPrice("");
      setImageUrl("");
      setShowForm(false);  // Hide the form after successful submission
    } catch (error) {
      console.error("Error adding product:", error.message);
      alert("Failed to add product.");
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteProduct = async (productId) => {
    const productDoc = doc(db, "products", productId);
    try {
      await deleteDoc(productDoc);
      alert("Product deleted successfully!");
      setProducts(products.filter((product) => product.id !== productId));
    } catch (error) {
      console.error("Error deleting product:", error.message);
      alert("Failed to delete product.");
    }
  };

  const handleDeleteOrder = async (orderId) => {
    const orderDoc = doc(db, "orders", orderId);
    try {
      await deleteDoc(orderDoc);
      alert("Order deleted successfully!");
      setOrders(orders.filter((order) => order.id !== orderId));
    } catch (error) {
      console.error("Error deleting order:", error.message);
      alert("Failed to delete order.");
    }
  };

  const handleSubmitImages = async (e) => {
    e.preventDefault();

    if (imageLinks.some((link) => !link) || titles.some((title) => !title)) {
      alert("Please fill in all image links and titles.");
      return;
    }

    setLoading(true);
    try {
      const adsCollection = collection(db, "headads");

      for (let i = 0; i < imageLinks.length; i++) {
        await addDoc(adsCollection, {
          imageUrl: imageLinks[i],
          title: titles[i],
        });
      }

      alert("Images added successfully!");
      setImageLinks(["", "", ""]);
      setTitles(["", "", ""]);
    } catch (error) {
      console.error("Error adding images:", error.message);
      alert("Failed to add images.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex justify-center min-h-screen p-5">
      <div className="w-full max-w-4xl">
        <h1 className="text-3xl font-bold text-center mb-6">Admin Panel</h1>

        {/* Toggle buttons */}
        <div className="flex gap-4 mb-6 justify-center">
          <button
            className="bg-blue-500 text-white px-6 py-2 rounded-md hover:bg-blue-600 transition"
            onClick={() => setShowForm(!showForm)}
          >
            {showForm ? "Hide Products" : "See Products"}
          </button>
          <button
            className="bg-green-500 text-white px-6 py-2 rounded-md hover:bg-green-600 transition"
            onClick={() => setShowImageForm(!showImageForm)}
          >
            {showImageForm ? "Hide News Form" : "Add News"}
          </button>
        </div>

        {/* Show Products */}
        {showForm && (
          <div className="mb-6">
            <h2 className="text-2xl text-center font-semibold mb-4">All Products</h2>
            <ul className="flex flex-wrap gap-4 justify-center">
              {products.length === 0 ? (
                <p>No products available.</p>
              ) : (
                products.map((product) => (
                  <li
                    key={product.id}
                    className="bg-gray-100 p-4 rounded-lg mb-4 w-full sm:w-1/2 md:w-1/3 lg:w-1/4"
                  >
                    <img
                      src={product.imageUrl}
                      alt={product.name}
                      className="w-full h-48 object-cover mb-4 rounded-md"
                    />
                    <div className="flex flex-col">
                      <h3 className="font-bold text-lg">{product.name}</h3>
                      <p className="text-gray-700">{product.description}</p>
                      <p className="text-gray-500">{`Price: $${product.price}`}</p>
                      <button
                        className="mt-4 bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600 transition"
                        onClick={() => handleDeleteProduct(product.id)}
                      >
                        Delete
                      </button>
                    </div>
                  </li>
                ))
              )}
            </ul>
          </div>
        )}

        {/* Product Form */}
        {showForm && (
          <form
            className="p-6 w-full sm:w-1/2 md:w-1/3 lg:w-1/4 mx-auto flex justify-center flex-col rounded-lg shadow-md"
            onSubmit={handleSubmitProduct}
          >
            {/* Form Fields */}
            {/* Repeating structure omitted for brevity */}
          </form>
        )}
      </div>
    </div>
  );
};

export default Admin;
