{cartModalOpen && (
    <div className="fixed inset-0 bg-black bg-opacity-80 flex justify-center items-center z-50">
        <div className="bg-gray-900 text-white w-[90%] max-w-5xl p-6 rounded-lg shadow-lg flex">
            {/* Left: Cart Items */}
            <div className="w-1/2 pr-4 border-r border-gray-700">
                <h2 className="text-2xl font-bold mb-4">Your Cart</h2>
                {cart.length > 0 ? (
                    <ul className="space-y-4">
                        {cart.map((item, index) => (
                            <li
                                key={index}
                                className="flex justify-between items-center border-b border-gray-700 pb-2"
                            >
                                <div>
                                    <h3 className="text-lg font-semibold">
                                        {item.name}
                                    </h3>
                                    <p className="text-sm">Size: {item.size}</p>
                                    <p className="text-sm">Quantity: {item.quantity}</p>
                                </div>
                                <button
                                    onClick={() =>
                                        setCart(cart.filter((_, i) => i !== index))
                                    }
                                    className="text-red-500 hover:text-red-600"
                                >
                                    Remove
                                </button>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>Your cart is empty.</p>
                )}
            </div>

            {/* Right: Order Form */}
            <div className="w-1/2 pl-4">
                <h2 className="text-2xl font-bold mb-4">Order Details</h2>
                <form
                    onSubmit={(e) => {
                        e.preventDefault();
                        handleSendOrder();
                    }}
                    className="space-y-4"
                >
                    <div>
                        <label className="block text-white mb-1">Name</label>
                        <input
                            type="text"
                            value={userName}
                            onChange={(e) => setUserName(e.target.value)}
                            className="w-full text-black border-gray-300 rounded p-2"
                            placeholder="Enter your name"
                        />
                    </div>
                    <div>
                        <label className="block text-white mb-1">Address</label>
                        <textarea
                            value={address}
                            onChange={(e) => setAddress(e.target.value)}
                            className="w-full text-black border-gray-300 rounded p-2"
                            placeholder="Enter your address"
                        />
                    </div>
                    <div>
                        <label className="block text-white mb-1">Phone</label>
                        <input
                            type="text"
                            value={phone}
                            onChange={(e) => setPhone(e.target.value)}
                            className="w-full text-black border-gray-300 rounded p-2"
                            placeholder="Enter your phone number"
                        />
                    </div>
                    <button
                        type="submit"
                        className="w-full bg-green-500 hover:bg-green-600 text-white py-2 rounded"
                    >
                        Send Order
