import React, { useState, useRef, useEffect } from "react";
import { collection, getDocs, addDoc } from "firebase/firestore";
import { db } from "../../firebase/connection";

const Products = () => {
    const [products, setProducts] = useState([]);
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [cart, setCart] = useState([]);
    const [quantity, setQuantity] = useState(1);
    const [size, setSize] = useState("5ml");
    const [phone, setPhone] = useState("");
    const [userName, setUserName] = useState("");
    const [isCartModalOpen, setIsCartModalOpen] = useState(false);
    const scrollerRef = useRef(null);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const querySnapshot = await getDocs(collection(db, "products"));
                const productsData = querySnapshot.docs.map((doc) => ({
                    id: doc.id,
                    ...doc.data(),
                }));
                setProducts(productsData);
                setFilteredProducts(productsData);
            } catch (error) {
                console.error("Error fetching products: ", error);
            }
        };

        fetchProducts();
    }, []);

    const handleSearchChange = (e) => {
        const value = e.target.value;
        setSearchTerm(value);

        if (value) {
            const filteredData = products.filter((product) =>
                product.name.toLowerCase().includes(value.toLowerCase())
            );
            setFilteredProducts(filteredData);
        } else {
            setFilteredProducts(products);
        }
    };

    const handleAddToCart = () => {
        const productDetails = {
            id: selectedProduct.id,
            name: selectedProduct.name,
            quantity,
            size,
        };
        setCart((prevCart) => [...prevCart, productDetails]);
        setSelectedProduct(null); // Close the individual product modal
    };

    const handleCartSubmit = async () => {
        if (!userName.trim() || !phone.trim() || cart.length === 0) {
            alert("Please fill in your details and add products to your cart.");
            return;
        }

        try {
            await addDoc(collection(db, "orders"), {
                userName: userName.trim(),
                phone: phone.trim(),
                cart,
                timestamp: new Date().toISOString(),
            });
            alert("Order placed successfully!");
            setCart([]);
            setIsCartModalOpen(false);
        } catch (error) {
            console.error("Error placing order: ", error);
            alert("Failed to place the order. Please try again later.");
        }
    };

    const scrollLeft = () => {
        if (scrollerRef.current) {
            scrollerRef.current.scrollBy({ left: -200, behavior: 'smooth' });
        }
    };

    const scrollRight = () => {
        if (scrollerRef.current) {
            scrollerRef.current.scrollBy({ left: 200, behavior: 'smooth' });
        }
    };

    return (
        <div id="products" className="pt-5 px-4 relative">
            {/* Fixed Button */}
            <button
                onClick={() => setIsCartModalOpen(true)}
                className="fixed bottom-10 right-1/2 transform translate-x-1/2 bg-blue-500 text-white w-24 h-24 rounded-full shadow-lg flex items-center justify-center hover:bg-blue-600"
            >
                <span className="text-lg font-bold">Cart</span>
            </button>

            {/* Main Content */}
            <div className="flex justify-center">
                <h1 className="text-2xl sm:text-3xl lg:text-4xl font-charm text-center">
                    Plupare Products
                </h1>
            </div>

            {/* Search Input */}
            <div className="flex w-[80%] mx-auto pt-5">
                <input
                    type="text"
                    value={searchTerm}
                    onChange={handleSearchChange}
                    className="flex-grow border-2 border-gray-300 rounded-l-md p-2 text-black focus:outline-none focus:ring-2 focus:ring-gray-400"
                    placeholder="Search for a product..."
                />
                <button
                    type="submit"
                    className="bg-gray-300 px-4 py-2 rounded-r-md border-l-2 border-gray-400 hover:scale-110 transform transition-transform duration-200 ease-in-out focus:ring-2 focus:ring-gray-400 focus:outline-none"
                >
                    <img
                        src="https://cdn-icons-png.flaticon.com/128/775/775307.png"
                        alt="Search Icon"
                        className="h-6 w-6"
                    />
                </button>
            </div>

            {/* Products Section */}
            <div className="mt-10 relative">
                <div
                    ref={scrollerRef}
                    className="flex overflow-x-auto gap-6 scrollbar-hide py-4"
                >
                    {filteredProducts.map((product) => (
                        <div
                            key={product.id}
                            className="min-w-[250px] flex-shrink-0 flex justify-center items-center flex-col shadow-lg rounded-lg p-6 border-4 border-white"
                        >
                            <img
                                src={product.imageUrl}
                                alt={product.name}
                                className="w-full h-[200px] rounded-lg object-cover mb-6"
                            />
                            <h1 className="text-xl font-bold font-charm mb-4">{product.name}</h1>
                            <p className="text-base text-gray-500 mb-6">{product.description}</p>
                            <h1 className="text-lg font-semibold mb-6">{product.price}</h1>
                            <button
                                onClick={() => setSelectedProduct(product)}
                                className="text-white px-6 py-3 border-white border-2 rounded-lg hover:bg-gray-600 transition duration-300"
                            >
                                VIEW PRODUCT
                            </button>
                        </div>
                    ))}
                </div>
            </div>

            {/* Selected Product Modal */}
            {selectedProduct && (
                <div className="fixed inset-0 bg-black bg-opacity-70 flex justify-center items-center">
                    <div className="bg-white p-6 rounded-lg shadow-lg w-[90%] max-w-4xl flex flex-col">
                        <h1 className="text-2xl font-bold mb-4">{selectedProduct.name}</h1>
                        <div className="space-y-4">
                            <div>
                                <label className="block text-gray-700 mb-1">Quantity</label>
                                <input
                                    type="number"
                                    value={quantity}
                                    onChange={(e) => setQuantity(Number(e.target.value))}
                                    className="w-full border-gray-300 rounded p-2"
                                />
                            </div>
                            <div>
                                <label className="block text-gray-700 mb-1">Size</label>
                                <select
                                    value={size}
                                    onChange={(e) => setSize(e.target.value)}
                                    className="w-full border-gray-300 rounded p-2"
                                >
                                    <option value="5ml">5ml</option>
                                    <option value="10ml">10ml</option>
                                </select>
                            </div>
                        </div>
                        <button
                            onClick={handleAddToCart}
                            className="w-full bg-blue-500 text-white py-2 rounded mt-4 hover:bg-blue-600"
                        >
                            Add to Cart
                        </button>
                        <button
                            onClick={() => setSelectedProduct(null)}
                            className="w-full bg-red-500 text-white py-2 rounded mt-2 hover:bg-red-600"
                        >
                            Close
                        </button>
                    </div>
                </div>
            )}

            {/* Cart Modal */}
            {isCartModalOpen && (
                <div className="fixed inset-0 bg-black bg-opacity-70 flex justify-center items-center">
                    <div className="bg-white p-6 rounded-lg shadow-lg w-[90%] max-w-4xl">
                        <h1 className="text-2xl font-bold mb-4">Your Cart</h1>
                        {cart.length > 0 ? (
                            <ul className="space-y-4">
                                {cart.map((item, index) => (
                                    <li key={index} className="flex justify-between items-center">
                                        <div>
                                            <h2 className="text-lg font-semibold">{item.name}</h2>
                                            <p>Size: {item.size}</p>
                                            <p>Quantity: {item.quantity}</p>
                                        </div>
                                    </li>
                                ))}
                            </ul>
                        ) : (
                            <p>Your cart is empty.</p>
                        )}
                        <button
                            onClick={handleCartSubmit}
                            className="w-full bg-green-500 text-white py-2 rounded mt-4 hover:bg-green-600"
                        >
                            Submit Order
                        </button>
                        <button
                            onClick={() => setIsCartModalOpen(false)}
                            className="w-full bg-red-500 text-white py-2 rounded mt-2 hover:bg-red-600"
                        >
                            Close
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Products;
