  if (showImageForm) {
    const fetchNews = async () => {
      try {
        setLoading(true);
        const adsCollection = collection(db, "headads");
        const adsSnapshot = await getDocs(adsCollection);
        const adsList = adsSnapshot.docs.map((doc) => ({ ...doc.data(), id: doc.id }));
        console.log("Fetched News:", adsList); // Log news to console
        setImageLinks(adsList.map((ad) => ad.imageUrl)); // Extract image URLs
      } catch (error) {
        console.error("Error fetching news:", error);
        alert("Failed to fetch news.");
      } finally {
        setLoading(false);
      }
    };

    fetchNews();
  }
}, [showImageForm]);

// Fetch users
useEffect(() => {
  const fetchUsers = async () => {
    try {
      const usersCollection = collection(db, "users");
      const userSnapshot = await getDocs(usersCollection);
      const userList = userSnapshot.docs.map((doc) => ({ ...doc.data(), id: doc.id }));
      console.log("Fetched Users:", userList); // Log users to console
