import { useState } from "react";
import { Link } from "react-router-dom";

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => setMenuOpen(!menuOpen);

  return (
    <header className="max-w-[1250px] flex items-center justify-between p-2 mx-auto mt-5 bg-gray-600 bg-opacity-50 border-2 border-white rounded-[30px] backdrop-blur-md sm:m-10 ">
            <h2 title="">
                <a href="/">
                    <img
                        src="/logo.png"
                        alt="Website Logo"
                        height="70px"
                        width="70px"
                        style={{ paddingLeft: "30px" }}
                    />
                </a>
            </h2>


      {/* Navigation Menu */}
      <nav>
        <ul
          className={`absolute left-0 top-0 w-full h-screen bg-gray-800 pr-3 flex flex-col items-center justify-center space-y-6 transform transition-transform duration-300 ease-in-out  ${menuOpen ? "translate-x-0" : "-translate-x-full"
            } sm:static sm:h-auto sm:w-auto sm:bg-transparent sm:flex sm:space-y-0 sm:space-x-6 sm:translate-x-0 sm:flex-row sm:text-xl`}
        >
          <li>
            <Link to="/career" onClick={toggleMenu}>
              <span className="text-white font-semibold hover:text-custom-pink cursor-pointer">
                Career
              </span>
            </Link>
          </li>
          <li>
            <Link to="/blog" onClick={toggleMenu}>
              <span className="text-white font-semibold hover:text-custom-pink cursor-pointer">
                Blog
              </span>
            </Link>
          </li>
          <li>
            <Link to="/contact" onClick={toggleMenu}>
              <span className="text-white font-semibold hover:text-custom-pink  cursor-pointer">
                Contact
              </span>
            </Link>
          </li>
        </ul>
      </nav>

      {/* Hamburger Menu */}
      <div
        className="z-50 flex flex-col justify-center items-center sm:hidden cursor-pointer"
        onClick={toggleMenu}
      >
        <span
          className={`h-0.5 w-7 mb-1.5 bg-white transform transition-transform duration-300 ${menuOpen ? "rotate-45 translate-y-2" : ""
            }`}
        ></span>
        <span
          className={`h-0.5 w-7 mb-1.5 bg-white transition ${menuOpen ? "opacity-0" : ""
            }`}
        ></span>
        <span
          className={`h-0.5 w-7 mb-1.5 bg-white transform transition-transform duration-300 ${menuOpen ? "-rotate-45 -translate-y-2" : ""
            }`}
        ></span>
      </div>
    </header>
  );
};

export default Navbar;
