import React, { useState } from "react";
import { addDoc, collection } from "firebase/firestore";
import { db } from "../../firebase/connection";

const Admin = () => {
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    price: "",
    imageUrl: "",
  });

  const [imageFormData, setImageFormData] = useState({
    heading: "",
    imageLinks: ["", "", ""],
    titles: ["", "", ""],
  });

  const [loading, setLoading] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [showImageForm, setShowImageForm] = useState(false);

  // Reset functions
  const resetProductForm = () => {
    setFormData({ name: "", description: "", price: "", imageUrl: "" });
  };

  const resetImageForm = () => {
    setImageFormData({ heading: "", imageLinks: ["", "", ""], titles: ["", "", ""] });
  };

  // Generic handle change for input fields
  const handleInputChange = (field, value) => {
    setFormData((prev) => ({ ...prev, [field]: value }));
  };

  const handleImageFormChange = (field, value, index = null) => {
    if (index !== null) {
      setImageFormData((prev) => ({
        ...prev,
        [field]: prev[field].map((item, i) => (i === index ? value : item)),
      }));
    } else {
      setImageFormData((prev) => ({ ...prev, [field]: value }));
    }
  };

  // Handle submitting a product
  const handleSubmitProduct = async (e) => {
    e.preventDefault();
    const { name, description, price, imageUrl } = formData;

    if (!name || !description || !price || !imageUrl) {
      alert("Please fill in all fields.");
      return;
    }

    setLoading(true);
    try {
      const productsCollection = collection(db, "products");
      await addDoc(productsCollection, { name, description, price, imageUrl });
      alert("Product added successfully!");
      resetProductForm();
    } catch (error) {
      console.error("Error adding product:", error.message);
      alert("Failed to add product.");
    } finally {
      setLoading(false);
    }
  };

  // Handle submitting images
  const handleSubmitImages = async (e) => {
    e.preventDefault();
    const { heading, imageLinks, titles } = imageFormData;

    if (!heading || imageLinks.some((link) => !link) || titles.some((title) => !title)) {
      alert("Please fill in all image links, titles, and heading.");
      return;
    }

    setLoading(true);
    try {
      const adsCollection = collection(db, "headads");
      const promises = imageLinks.map((link, i) =>
        addDoc(adsCollection, { imageUrl: link, title: titles[i], heading })
      );
      await Promise.all(promises);
      alert("Images added successfully!");
      resetImageForm();
    } catch (error) {
      console.error("Error adding images:", error.message);
      alert("Failed to add images. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-lg mx-auto p-5">
      <h1 className="text-3xl font-bold text-center mb-6">Admin Panel</h1>

      {/* Toggle buttons */}
      <div className="flex gap-4 mb-6">
        <button
          className="bg-blue-500 text-white px-6 py-2 rounded-md hover:bg-blue-600 transition"
          onClick={() => setShowForm(!showForm)}
        >
          {showForm ? "Hide Product Form" : "Add Product"}
        </button>
        <button
          className="bg-green-500 text-white px-6 py-2 rounded-md hover:bg-green-600 transition"
          onClick={() => setShowImageForm(!showImageForm)}
        >
          {showImageForm ? "Hide News Form" : "Add News"}
        </button>
      </div>

      {/* Product Form */}
      {showForm && (
        <form className="bg-gray-100 p-6 rounded-lg shadow-md" onSubmit={handleSubmitProduct}>
          {Object.entries(formData).map(([field, value]) => (
            <div className="mb-4" key={field}>
              <label htmlFor={field} className="block text-gray-700 font-medium mb-2">
                {field.charAt(0).toUpperCase() + field.slice(1)}
              </label>
              <input
                id={field}
                type={field === "price" ? "number" : "text"}
                value={value}
                onChange={(e) => handleInputChange(field, e.target.value)}
                className="w-full border border-gray-300 rounded-md p-2 focus:ring-2 focus:ring-blue-500 text-black"
                required
              />
            </div>
          ))}
          <button
            type="submit"
            className={`w-full bg-green-500 text-white py-2 rounded-md hover:bg-green-600 transition ${loading ? "opacity-50 cursor-not-allowed" : ""}`}
            disabled={loading}
          >
            {loading ? "Adding..." : "Add Product"}
          </button>
        </form>
      )}

      {/* Images Form */}
      {showImageForm && (
        <form className="bg-gray-100 p-6 rounded-lg shadow-md" onSubmit={handleSubmitImages}>
          <div className="mb-4">
            <label htmlFor="heading" className="block text-gray-700 font-medium mb-2">
              Heading
            </label>
            <input
              id="heading"
              type="text"
              value={imageFormData.heading}
              onChange={(e) => handleImageFormChange("heading", e.target.value)}
              className="w-full border border-gray-300 rounded-md p-2 focus:ring-2 focus:ring-blue-500 text-black"
              required
            />
          </div>

          {imageFormData.imageLinks.map((link, idx) => (
            <div className="mb-4" key={idx}>
              <label htmlFor={`image-${idx}`} className="block text-gray-700 font-medium mb-2">
                Image {idx + 1} URL
              </label>
              <input
                id={`image-${idx}`}
                type="url"
                value={link}
                onChange={(e) => handleImageFormChange("imageLinks", e.target.value, idx)}
                className="w-full border border-gray-300 rounded-md p-2 focus:ring-2 focus:ring-blue-500 text-black"
                required
              />
              <label htmlFor={`title-${idx}`} className="block text-gray-700 font-medium mb-2">
                Image {idx + 1} Title
              </label>
              <input
                id={`title-${idx}`}
                type="text"
                value={imageFormData.titles[idx]}
                onChange={(e) => handleImageFormChange("titles", e.target.value, idx)}
                className="w-full border border-gray-300 rounded-md p-2 focus:ring-2 focus:ring-blue-500 text-black"
                required
              />
            </div>
          ))}

          <button
            type="submit"
            className={`w-full bg-green-500 text-white py-2 rounded-md hover:bg-green-600 transition ${loading ? "opacity-50 cursor-not-allowed" : ""}`}
            disabled={loading}
          >
            {loading ? "Adding..." : "Add News"}
          </button>
        </form>
      )}
    </div>
  );
};

export default Admin;
