import React, { useState } from 'react';
import { addDoc, collection } from 'firebase/firestore';
import { db } from '../../firebase/connection';

const Admin = () => {
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [price, setPrice] = useState('');
    const [imageUrl, setImageUrl] = useState('');
    const [loading, setLoading] = useState(false);

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();

        // Validate fields
        if (!name || !description || !price || !imageUrl) {
            alert("Please fill in all fields.");
            return;
        }

        if (!/^(https?:\/\/)/.test(imageUrl)) {
            alert("Please enter a valid URL.");
            return;
        }

        if (parseFloat(price) <= 0) {
            alert("Please enter a valid price.");
            return;
        }

        setLoading(true);

        try {
            // Debug log to check data
            console.log("Sending data:", { name, description, price, imageUrl });

            // Add product data to Firestore
            const productsCollection = collection(db, "products");
            await addDoc(productsCollection, {
                name,
                description,
                price: parseFloat(price), // Ensure price is stored as a number
                imageUrl,
            });

            // Reset form
            setName('');
            setDescription('');
            setPrice('');
            setImageUrl('');

            alert("Product added successfully!");
        } catch (error) {
            console.error("Error adding product: ", error);
            alert("Failed to add product.");
        }

        setLoading(false);
    };

    return (
        <form onSubmit={handleSubmit} className="space-y-4 p-6 max-w-md mx-auto border rounded-lg shadow-lg bg-white">
            <h1 className="text-2xl font-bold text-center">Add Product</h1>
            <div>
                <label htmlFor="name" className="block text-lg font-medium">Product Name</label>
                <input
                    id="name"
                    type="text"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    className="w-full p-3 border rounded-lg focus:outline-none focus:ring focus:ring-blue-300"
                    placeholder="Enter product name"
                    required
                />
            </div>
            <div>
                <label htmlFor="description" className="block text-lg font-medium">Description</label>
                <textarea
                    id="description"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    className="w-full p-3 border rounded-lg focus:outline-none focus:ring focus:ring-blue-300"
                    placeholder="Enter product description"
                    required
                />
            </div>
            <div>
                <label htmlFor="price" className="block text-lg font-medium">Price</label>
                <input
                    id="price"
                    type="number"
                    value={price}
                    onChange={(e) => setPrice(e.target.value)}
                    className="w-full p-3 border rounded-lg focus:outline-none focus:ring focus:ring-blue-300"
                    placeholder="Enter product price"
                    required
                />
            </div>
            <div>
                <label htmlFor="imageUrl" className="block text-lg font-medium">Image URL</label>
                <input
                    id="imageUrl"
                    type="url"
                    value={imageUrl}
                    onChange={(e) => setImageUrl(e.target.value)}
                    className="w-full p-3 border rounded-lg focus:outline-none focus:ring focus:ring-blue-300"
                    placeholder="Enter image URL"
                    required
                />
            </div>
            <div className="flex justify-center">
                <button
                    type="submit"
                    className={`bg-blue-500 text-white px-6 py-3 rounded-lg hover:bg-blue-600 transition duration-300 ${loading ? 'opacity-50' : ''}`}
                    disabled={loading}
                >
                    {loading ? 'Adding...' : 'Add Product'}
                </button>
            </div>
        </form>
    );
};

export default Admin;
