import { useState, useRef } from "react";

const Products = () => {
    const [selectedOption, setSelectedOption] = useState("");
    const scrollerRef = useRef(null);

    const handleSelectChange = (event) => {
        setSelectedOption(event.target.value);
        console.log("Selected:", event.target.value);
    };

    const scrollLeft = () => {
        scrollerRef.current.scrollBy({ left: -300, behavior: "smooth" });
    };

    const scrollRight = () => {
        scrollerRef.current.scrollBy({ left: 300, behavior: "smooth" });
    };

    return (
        <div className="pt-5 px-4">
            {/* Heading */}
            <div className="flex justify-center">
                <h1 className="text-2xl sm:text-3xl lg:text-4xl font-charm text-center">
                    Plupare Products
                </h1>
            </div>

            {/* Select, Input, and Search */}
            <div className="flex flex-col sm:flex-row justify-around items-center pt-8 gap-6 sm:gap-4">

                {/* Search Input */}
                <div className="flex w-full sm:w-1/3">
                    <input
                        type="text"
                        className="flex-grow border-2 border-gray-300 border-r-0 rounded-l-md p-2 text-black focus:outline-none focus:ring-2 focus:ring-gray-400"
                        placeholder="What are you looking for?"
                    />
                    <button
                        type="submit"
                        className="bg-gray-300 px-4 py-2 rounded-r-md border-l-2 border-gray-400 hover:scale-110 transform transition-transform duration-200 ease-in-out focus:ring-2 focus:ring-gray-400 focus:outline-none"
                    >
                        <img
                            src="https://cdn-icons-png.flaticon.com/128/775/775307.png"
                            alt="Search Icon"
                            className="h-6 w-6"
                        />
                    </button>
                </div>
            </div>

            {/* Products Section */}
            <div className="mt-10 relative">
                {/* Scroller */}
                <div
                    ref={scrollerRef}
                    className="flex overflow-x-auto gap-6 scrollbar-hide py-4"
                    style={{
                        scrollbarWidth: 'none',
                        msOverflowStyle: 'none',
                    }}
                >
                    {Array.from({ length: 10 }).map((_, index) => (
                        <div
                            key={index}
                            className="min-w-[250px] max-w-[300px] flex-shrink-0 bg-white shadow-lg rounded-lg p-4"
                        >
                            <img
                                src={`https://via.placeholder.com/300x200?text=Product+${index + 1}`}
                                alt={`Product ${index + 1}`}
                                className="w-full h-[200px] rounded-lg object-cover mb-4"
                            />
                            <h1 className="text-lg font-bold mb-2">Product {index + 1}</h1>
                            <p className="text-sm text-gray-500 mb-4">
                                This is a description for Product {index + 1}.
                            </p>
                            <h1 className="text-base font-semibold mb-4">Price: $50</h1>
                            <button className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition duration-300">
                                Add to Cart
                            </button>
                        </div>
                    ))}
                </div>

                {/* Scroll Buttons */}
                <div className="flex justify-center gap-4 mt-4">
                    <button
                        onClick={scrollLeft}
                        className="p-3 bg-gray-200 rounded-full hover:bg-gray-300 shadow-md"
                    >
                        &larr; Scroll Left
                    </button>
                    <button
                        onClick={scrollRight}
                        className="p-3 bg-gray-200 rounded-full hover:bg-gray-300 shadow-md"
                    >
                        Scroll Right &rarr;
                    </button>
                </div>
            </div>
        </div>
    );
};

export default Products;
