import React, { useState, useEffect } from "react";
import { addDoc, collection, getDocs, deleteDoc, doc } from "firebase/firestore";
import { db } from "../../firebase/connection";

const Admin = () => {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [heading, setHeading] = useState("");
  const [loading, setLoading] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [showImageForm, setShowImageForm] = useState(false);
  const [showOrders, setShowOrders] = useState(false);
  const [imageLinks, setImageLinks] = useState(["", "", ""]);
  const [titles, setTitles] = useState(["", "", ""]);
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [news, setNews] = useState([]); // Added state for storing news

  // Fetch products from Firestore
  useEffect(() => {
    if (showForm) {
      const fetchProducts = async () => {
        const productsCollection = collection(db, "products");
        const productSnapshot = await getDocs(productsCollection);
        const productList = productSnapshot.docs.map((doc) => ({ ...doc.data(), id: doc.id }));
        setProducts(productList);
      };

      fetchProducts();
    }
  }, [showForm]);

  // Fetch orders from Firestore (Only once)
  useEffect(() => {
    const fetchOrders = async () => {
      const ordersCollection = collection(db, "orders");
      const orderSnapshot = await getDocs(ordersCollection);
      const orderList = orderSnapshot.docs.map((doc) => ({
        ...doc.data(),
        id: doc.id
      }));
      setOrders(orderList);
    };

    if (orders.length === 0) {
      fetchOrders();
    }
  }, [orders.length]);

  // Handle adding product
  const handleSubmitProduct = async (e) => {
    e.preventDefault();

    if (!name || !description || !price || !imageUrl) {
      alert("Please fill in all fields.");
      return;
    }

    setLoading(true);
    try {
      const productsCollection = collection(db, "products");
      await addDoc(productsCollection, { name, description, price, imageUrl });
      alert("Product added successfully!");
      setName("");
      setDescription("");
      setPrice("");
      setImageUrl("");
      setShowForm(false);
    } catch (error) {
      console.error("Error adding product:", error.message);
      alert("Failed to add product.");
    } finally {
      setLoading(false);
    }
  };

  // Handle deleting product
  const handleDeleteProduct = async (productId) => {
    const productDoc = doc(db, "products", productId);
    try {
      await deleteDoc(productDoc);
      alert("Product deleted successfully!");
      setProducts(products.filter((product) => product.id !== productId));
    } catch (error) {
      console.error("Error deleting product:", error.message);
      alert("Failed to delete product.");
    }
  };

  // Handle deleting order
  const handleDeleteOrder = async (orderId) => {
    const orderDoc = doc(db, "orders", orderId);
    try {
      await deleteDoc(orderDoc);
      alert("Order deleted successfully!");
      setOrders(orders.filter((order) => order.id !== orderId));
    } catch (error) {
      console.error("Error deleting order:", error.message);
      alert("Failed to delete order.");
    }
  };

  // Handle adding images for news
  const handleSubmitImages = async (e) => {
    e.preventDefault();

    if (imageLinks.some((link) => !link) || titles.some((title) => !title) || !heading) {
      alert("Please fill in all image links, titles, and heading.");
      return;
    }

    setLoading(true);
    try {
      const adsCollection = collection(db, "headads");

      // Add the news items
      const newNews = imageLinks.map((link, idx) => ({
        imageUrl: link,
        title: titles[idx],
        heading,
      }));

      // Save news to state (for rendering)
      setNews(newNews);

      // Optionally, save the news to Firestore
      for (let i = 0; i < newNews.length; i++) {
        await addDoc(adsCollection, newNews[i]);
      }

      alert("Images added successfully!");
      setImageLinks(["", "", ""]);
      setTitles(["", "", ""]);
      setHeading("");
    } catch (error) {
      console.error("Error adding images:", error.message);
      alert("Failed to add images.");
    } finally {
      setLoading(false);
    }
  };

  // Handle dynamic changes for Image Links and Titles
  const handleImageLinkChange = (e, idx) => {
    const updatedLinks = [...imageLinks];
    updatedLinks[idx] = e.target.value;
    setImageLinks(updatedLinks);
  };

  const handleTitleChange = (e, idx) => {
    const updatedTitles = [...titles];
    updatedTitles[idx] = e.target.value;
    setTitles(updatedTitles);
  };

  // Handle deleting news item
  const handleDeleteNews = (index) => {
    setNews(news.filter((_, idx) => idx !== index));
  };

  return (
    <div className="flex justify-center min-h-screen p-5">
      <div className="w-full max-w-4xl">
        <h1 className="text-3xl font-bold text-center mb-6">Admin Panel</h1>

        {/* Toggle buttons */}
        <div className="flex gap-4 mb-6 justify-center">
          <button
            className="bg-blue-500 text-white px-6 py-2 rounded-md hover:bg-blue-600 transition"
            onClick={() => setShowForm(!showForm)}
          >
            {showForm ? "Hide Products" : "See Products"}
          </button>
          <button
            className="bg-green-500 text-white px-6 py-2 rounded-md hover:bg-green-600 transition"
            onClick={() => setShowImageForm(!showImageForm)}
          >
            {showImageForm ? "Hide News Form" : "Add News"}
          </button>
          <button
            className="bg-purple-500 text-white px-6 py-2 rounded-md hover:bg-purple-600 transition"
            onClick={() => setShowOrders(!showOrders)}
          >
            {showOrders ? "Hide Orders" : "See Orders"}
          </button>
        </div>

        {/* Show Products */}
        {showForm && (
          <div className="mb-6">
            <h2 className="text-2xl text-center font-semibold mb-4">All Products</h2>
            <ul className="flex flex-wrap gap-4 justify-center">
              {products.length === 0 ? (
                <p>No products available.</p>
              ) : (
                products.map((product) => (
                  <li
                    key={product.id}
                    className="bg-gray-100 p-4 rounded-lg mb-4 w-full sm:w-1/2 md:w-1/3 lg:w-1/4"
                  >
                    <img
                      src={product.imageUrl}
                      alt={product.name}
                      className="w-full h-48 object-cover mb-4 rounded-md"
                    />
                    <div className="flex flex-col">
                      <h3 className="font-bold text-lg">{product.name}</h3>
                      <p className="text-gray-700">{product.description}</p>
                      <p className="text-gray-500">{`Price: $${product.price}`}</p>
                      <button
                        className="mt-4 bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600 transition"
                        onClick={() => handleDeleteProduct(product.id)}
                      >
                        Delete
                      </button>
                    </div>
                  </li>
                ))
              )}
            </ul>
          </div>
        )}

        {/* Show Orders */}
        {showOrders && orders.length > 0 && (
          <div className="mb-6">
            <h2 className="text-2xl text-center font-semibold mb-4">All Orders</h2>
            <ul className="flex flex-wrap gap-4 justify-center">
              {orders.map((order) => (
                <li
                  key={order.id}
                  className="bg-black border-2 border-white p-4 rounded-lg mb-4 w-full sm:w-1/2 md:w-1/3 lg:w-1/4"
                >
                  <div className="flex flex-col">
                    <h3 className="font-bold text-lg text-white">Phone: {order.phone}</h3>
                    <p className="text-white">Product Name: {order.productName}</p>
                    <p className="text-white">Price: {order.price}</p>
                    <button
                      className="mt-4 bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600 transition"
                      onClick={() => handleDeleteOrder(order.id)}
                    >
                      Delete Order
                    </button>
                  </div>
                </li>
              ))}
            </ul>
          </div>
        )}

        {/* Add News Form */}
        {showImageForm && (
          <form className="mb-6" onSubmit={handleSubmitImages}>
            <div className="flex gap-4 flex-wrap justify-center mb-4">
              <div className="w-1/3">
                <label htmlFor="heading" className="block text-sm font-medium">
                  Heading
                </label>
                <input
                  type="text"
                  id="heading"
                  value={heading}
                  onChange={(e) => setHeading(e.target.value)}
                  className="mt-1 block w-full p-2 border rounded-md"
                />
              </div>
            </div>

            {[0, 1, 2].map((idx) => (
              <div key={idx} className="flex gap-4 flex-wrap justify-center mb-4">
                <div className="w-1/3">
                  <label htmlFor={`imageLink${idx}`} className="block text-sm font-medium">
                    Image URL {idx + 1}
                  </label>
                  <input
                    type="text"
                    id={`imageLink${idx}`}
                    value={imageLinks[idx]}
                    onChange={(e) => handleImageLinkChange(e, idx)}
                    className="mt-1 block w-full p-2 border rounded-md"
                  />
                </div>
                <div className="w-1/3">
                  <label htmlFor={`title${idx}`} className="block text-sm font-medium">
                    Title {idx + 1}
                  </label>
                  <input
                    type="text"
                    id={`title${idx}`}
                    value={titles[idx]}
                    onChange={(e) => handleTitleChange(e, idx)}
                    className="mt-1 block w-full p-2 border rounded-md"
                  />
                </div>
              </div>
            ))}

            <button
              type="submit"
              className="bg-blue-500 text-white px-6 py-2 rounded-md hover:bg-blue-600 transition"
            >
              {loading ? "Loading..." : "Add News"}
            </button>
          </form>
        )}

        {/* Display News */}
        {news.length > 0 && (
          <div className="mt-8">
            <h2 className="text-2xl font-semibold text-center mb-4">News</h2>
            <ul className="flex flex-wrap gap-4 justify-center">
              {news.map((newsItem, idx) => (
                <li key={idx} className="bg-white p-4 rounded-lg mb-4 w-full sm:w-1/2 md:w-1/3 lg:w-1/4">
                  <img
                    src={newsItem.imageUrl}
                    alt={newsItem.title}
                    className="w-full h-48 object-cover mb-4 rounded-md"
                  />
                  <div className="flex flex-col">
                    <h3 className="font-bold text-lg">{newsItem.title}</h3>
                    <p className="text-gray-700">{newsItem.heading}</p>
                    <button
                      className="mt-4 bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600 transition"
                      onClick={() => handleDeleteNews(idx)}
                    >
                      Delete News
                    </button>
                  </div>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default Admin;
