import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [selectedOption, setSelectedOption] = useState("");
  const navigate = useNavigate(); // Use useNavigate for navigation

  const toggleMenu = () => setMenuOpen(!menuOpen);

  const handleSelectChange = (e) => {
    const value = e.target.value;
    setSelectedOption(value);
    if (value) {
      navigate(`/${value}`);
    }
  };

  return (
    <header className="px-2 max-w-[320px] md:max-w-[750px] lg:max-w-[1250px] h-14 flex items-center justify-between mx-auto rounded-full flex-wrap bg-black mt-10 backdrop-blur-md border-2 border-dashed">
      {/* Logo */}
      <div className="flex justify-center items-center">
        <h2 className="text-5xl text-white font-bold pl-3 h-8">
          <a href="/">
            <img
              src="/name.png"
              alt="logo"
              className="-mt-1 max-w-[100px] w-full"
              height="50px"

            />
          </a>
        </h2>
      </div>

      {/* Navigation Menu */}
      <nav className="flex justify-center items-center">
        <ul
          className={` bg-green-400 absolute left-0 -top-3 w-full h-screen bg-black pr-3 flex flex-col items-center justify-center space-y-6 transform transition-transform duration-300 ease-in-out  ${menuOpen ? "translate-x-0 fixed -top-3 left-0 right-0 bottom-0 z-40 h-52" : "hidden"
            } sm:static sm:h-auto sm:w-auto sm:bg-transparent sm:flex sm:space-y-0 sm:space-x-6 sm:translate-x-0 sm:flex-row sm:text-xl`}
        >
          <li>
            <Link to="/career" onClick={toggleMenu}>
              <span className="text-white font-semibold hover:text-custom-pink cursor-pointer">
                Career
              </span>
            </Link>
          </li>
          <li>
            <select
              value={selectedOption}
              onChange={handleSelectChange}
              className="bg-black text-white font-semibold cursor-pointer p-2 rounded-md w-34 "
            >
              <option value="" disabled>
                Men/Women
              </option>
              <option value="men">Men</option>
              <option value="women">Women</option>
            </select>
          </li>
          <li>
            <Link to="/contact" onClick={toggleMenu}>
              <span className="text-white font-semibold hover:text-custom-pink cursor-pointer">
                Contact
              </span>
            </Link>
          </li>
        </ul>
      </nav>

      {/* Hamburger Menu */}
      <div
        className="z-50 flex flex-col justify-center items-center sm:hidden cursor-pointer"
        onClick={toggleMenu}
      >
        <span
          className={`h-0.5 w-7 mb-1.5 bg-white transform transition-transform duration-300 ${menuOpen ? "rotate-45 translate-y-2" : ""}`}
        ></span>
        <span
          className={`h-0.5 w-7 mb-1.5 bg-white transition ${menuOpen ? "opacity-0" : ""}`}
        ></span>
        <span
          className={`h-0.5 w-7 mb-1.5 bg-white transform transition-transform duration-300 ${menuOpen ? "-rotate-45 -translate-y-2" : ""}`}
        ></span>
      </div>
    </header>
  );
};

export default Navbar;
