import { useState } from "react";

const Headads = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div className="flex flex-col gap-6 px-4">
      {/* Heading */}
      <h1 className="pt-10 text-lg sm:text-xl md:text-3xl lg:text-6xl flex justify-center font-bold font-charm text-center">
        Let your presence linger with our captivating aromas.
      </h1>

      {/* Image Section */}
      <div className="flex flex-wrap gap-4 pt-8">
        {/* First Image */}
        <div className="group relative flex-1 min-w-[20%] sm:min-w-[48%] md:min-w-[30%] lg:min-w-[30%]">
          <h1 className="absolute bottom-2 right-2 text-white text-lg font-bold font-charm z-10 px-4 py-2 rounded-md opacity-0 group-hover:opacity-100 transition-opacity duration-300 ease-in-out">
            La Nuit de L'Homme, Yves Saint Laurent
          </h1>
          <img
            src="https://i.pinimg.com/736x/75/fd/8f/75fd8f9ab905b616eaf857252898dfcc.jpg"
            alt="La Nuit de L'Homme, Yves Saint Laurent"
            className="w-full h-full rounded-3xl border-2 border-white group-hover:scale-105 transition-transform duration-300 ease-in-out"
          />
        </div>

        {/* Second Image */}
        <div className="group relative flex-1 min-w-[20%] sm:min-w-[48%] md:min-w-[30%] lg:min-w-[30%]">
          <h1 className="absolute bottom-2 right-2 text-white text-lg font-bold font-charm z-10 px-4 py-2 rounded-md opacity-0 group-hover:opacity-100 transition-opacity duration-300 ease-in-out">
            Stronger With You, Giorgio Armani
          </h1>
          <img
            src="https://i.pinimg.com/736x/fd/51/49/fd5149878530a3c45fd350b18bf5afef.jpg"
            alt="Stronger With You"
            className="w-full h-full rounded-3xl border-2 border-white group-hover:scale-105 transition-transform duration-300 ease-in-out"
          />
        </div>

        {/* Third Image */}
        <div className="group relative flex-1 min-w-[70%] sm:min-w-[48%] md:min-w-[30%] lg:min-w-[30%]">
          <h1 className="absolute bottom-2 right-2 text-white text-lg font-bold font-charm z-10 px-4 py-2 rounded-md opacity-0 group-hover:opacity-100 transition-opacity duration-300 ease-in-out">
            Sauvage Elixir, DIOR
          </h1>
          <img
            src="https://i.pinimg.com/736x/95/f9/a0/95f9a0c4727c2e8c96acd836c031363d.jpg"
            alt="Sauvage Elixir, DIOR"
            className="w-full h-full rounded-3xl border-2 border-white group-hover:scale-105 transition-transform duration-300 ease-in-out"
          />
        </div>
      </div>

      {/* Scroller Section */}
      <div className="relative">
        <div className="flex overflow-x-auto gap-6 py-4 px-6 scrollbar-hide">
          {Array.from({ length: 10 }).map((_, index) => (
            <div
              key={index}
              className="min-w-[250px] max-w-[300px] flex-shrink-0 bg-white shadow-lg rounded-lg p-4"
            >
              <img
                src={`https://via.placeholder.com/300x200?text=Card+${index + 1}`}
                alt={`Card ${index + 1}`}
                className="w-full h-[200px] rounded-lg object-cover mb-4"
              />
              <h1 className="text-lg font-bold mb-2">Card Title {index + 1}</h1>
              <p className="text-sm text-gray-500 mb-4">
                This is a description for Card {index + 1}.
              </p>
              <h1 className="text-base font-semibold mb-4">Price: $50</h1>
              <button
                onClick={openModal}
                className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition duration-300"
              >
                Open Modal
              </button>
            </div>
          ))}
        </div>
      </div>

      {/* Modal Section */}
      {isModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-white w-[90%] max-w-md p-6 rounded-lg shadow-lg relative">
            <button
              onClick={closeModal}
              className="absolute top-4 right-4 text-gray-500 hover:text-gray-800"
            >
              &times;
            </button>
            <div className="text-center">
              <h1 className="text-lg font-bold">Modal</h1>
              <p>Customize this modal as needed.</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Headads;
