import React, { useState } from "react";
import { addDoc, collection } from "firebase/firestore";
import { db } from "../../firebase/connection";

const Admin = () => {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [heading, setHeading] = useState("");
  const [loading, setLoading] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [showImageForm, setShowImageForm] = useState(false);
  const [imageLinks, setImageLinks] = useState(["", "", ""]);
  const [titles, setTitles] = useState(["", "", ""]);

  const resetForm = () => {
    setName("");
    setDescription("");
    setPrice("");
    setImageUrl("");
  };

  const resetImageForm = () => {
    setImageLinks(["", "", ""]);
    setTitles(["", "", ""]);
    setHeading("");
  };

  // Handle adding images
  const handleSubmitImages = async (e) => {
    e.preventDefault();
    if (imageLinks.some((link) => !link) || titles.some((title) => !title) || !heading) {
      alert("Please fill in all image links, titles, and heading.");
      return;
    }

    setLoading(true);
    try {
      const adsCollection = collection(db, "headads");
      for (let i = 0; i < imageLinks.length; i++) {
        await addDoc(adsCollection, {
          imageUrl: imageLinks[i],
          title: titles[i],
          heading,
        });
      }
      alert("Images added successfully!");
      resetImageForm();
    } catch (error) {
      console.error("Error adding images:", error.message);
      alert("Failed to add images. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (setter) => (e) => setter(e.target.value);

  const handleArrayChange = (setter, array, index) => (e) => {
    const updatedArray = [...array];
    updatedArray[index] = e.target.value;
    setter(updatedArray);
  };

  return (
    <div className="max-w-lg mx-auto p-5">
      <h1 className="text-3xl font-bold text-center mb-6">Admin Panel</h1>

      {/* Toggle buttons */}
      <div className="flex gap-4 mb-6">
        <button
          className="bg-blue-500 text-white px-6 py-2 rounded-md hover:bg-blue-600 transition"
          onClick={() => setShowForm(!showForm)}
        >
          {showForm ? "Hide Product Form" : "Add Product"}
        </button>
        <button
          className="bg-green-500 text-white px-6 py-2 rounded-md hover:bg-green-600 transition"
          onClick={() => setShowImageForm(!showImageForm)}
        >
          {showImageForm ? "Hide News Form" : "Add News"}
        </button>
      </div>

      {/* Product Form */}
      {showForm && (
        <form className="bg-gray-100 p-6 rounded-lg shadow-md" onSubmit={handleSubmitProduct}>
          <div className="mb-4">
            <label htmlFor="name" className="block text-gray-700 font-medium mb-2">
              Product Name
            </label>
            <input
              id="name"
              type="text"
              value={name}
              onChange={handleInputChange(setName)}
              className="w-full border border-gray-300 rounded-md p-2 focus:ring-2 focus:ring-blue-500 text-black"
              required
            />
          </div>
          <div className="mb-4">
            <label htmlFor="description" className="block text-gray-700 font-medium mb-2">
              Description
            </label>
            <textarea
              id="description"
              value={description}
              onChange={handleInputChange(setDescription)}
              className="w-full border border-gray-300 rounded-md p-2 focus:ring-2 focus:ring-blue-500 text-black"
              required
            />
          </div>
          <div className="mb-4">
            <label htmlFor="price" className="block text-gray-700 font-medium mb-2">
              Price
            </label>
            <input
              id="price"
              type="text"
              value={price}
              onChange={handleInputChange(setPrice)}
              className="w-full border border-gray-300 rounded-md p-2 focus:ring-2 focus:ring-blue-500 text-black"
              required
            />
          </div>
          <div className="mb-4">
            <label htmlFor="imageUrl" className="block text-gray-700 font-medium mb-2">
              Image URL
            </label>
            <input
              id="imageUrl"
              type="url"
              value={imageUrl}
              onChange={handleInputChange(setImageUrl)}
              className="w-full border border-gray-300 rounded-md p-2 focus:ring-2 focus:ring-blue-500 text-black"
              required
            />
          </div>
          <button
            type="submit"
            className={`w-full bg-green-500 text-white py-2 rounded-md hover:bg-green-600 transition ${loading ? "opacity-50 cursor-not-allowed" : ""}`}
            disabled={loading}
          >
            {loading ? "Adding..." : "Add Product"}
          </button>
        </form>
      )}

      {/* Images Form */}
      {showImageForm && (
        <form className="bg-gray-100 p-6 rounded-lg shadow-md" onSubmit={handleSubmitImages}>
          <div className="mb-4">
            <label htmlFor="heading" className="block text-gray-700 font-medium mb-2">
              Heading
            </label>
            <input
              id="heading"
              type="text"
              value={heading}
              onChange={handleInputChange(setHeading)}
              className="w-full border border-gray-300 rounded-md p-2 focus:ring-2 focus:ring-blue-500 text-black"
              required
            />
          </div>

          {imageLinks.map((_, idx) => (
            <div className="mb-4" key={idx}>
              <label htmlFor={`image-${idx}`} className="block text-gray-700 font-medium mb-2">
                {`Image ${idx + 1} URL`}
              </label>
              <input
                id={`image-${idx}`}
                type="url"
                value={imageLinks[idx]}
                onChange={handleArrayChange(setImageLinks, imageLinks, idx)}
                className="w-full border border-gray-300 rounded-md p-2 focus:ring-2 focus:ring-blue-500 text-black"
                required
              />
              <label htmlFor={`title-${idx}`} className="block text-gray-700 font-medium mb-2">
                {`Image ${idx + 1} Title`}
              </label>
              <input
                id={`title-${idx}`}
                type="text"
                value={titles[idx]}
                onChange={handleArrayChange(setTitles, titles, idx)}
                className="w-full border border-gray-300 rounded-md p-2 focus:ring-2 focus:ring-blue-500 text-black"
                required
              />
            </div>
          ))}

          <button
            type="submit"
            className={`w-full bg-green-500 text-white py-2 rounded-md hover:bg-green-600 transition ${loading ? "opacity-50 cursor-not-allowed" : ""}`}
            disabled={loading}
          >
            {loading ? "Adding..." : "Add News"}
          </button>
        </form>
      )}
    </div>
  );
};

export default Admin;
