import { useEffect, useState, useRef } from "react";
import { collection, getDocs } from "firebase/firestore";
import { db } from "../../firebase/connection";

const Products = () => {
    const [products, setProducts] = useState([]);
    const [selectedOption, setSelectedOption] = useState("");
    const scrollerRef = useRef(null);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const productsCollection = collection(db, "products");
                const productSnapshot = await getDocs(productsCollection);
                const productList = productSnapshot.docs.map((doc) => ({
                    id: doc.id,
                    ...doc.data(),
                }));
                setProducts(productList);
            } catch (error) {
                console.error("Error fetching products: ", error);
            }
        };

        fetchProducts();
    }, []);

    const handleSelectChange = (event) => {
        setSelectedOption(event.target.value);
        console.log("Selected:", event.target.value);
    };

    const scrollLeft = () => {
        scrollerRef.current.scrollBy({ left: -300, behavior: "smooth" });
    };

    const scrollRight = () => {
        scrollerRef.current.scrollBy({ left: 300, behavior: "smooth" });
    };

    return (
        <div className="pt-5 px-4">
            {/* Heading */}
            <div className="flex justify-center">
                <h1 className="text-2xl sm:text-3xl lg:text-4xl font-charm text-center">
                    Plupare Products
                </h1>
            </div>

            {/* Select, Input, and Search */}
            <div className="flex flex-col sm:flex-row justify-around items-center pt-8 gap-6 sm:gap-4">
                {/* Select Dropdown */}
                <div className="w-full sm:w-auto hidden sm:block">
                    <select
                        value={selectedOption}
                        onChange={handleSelectChange}
                        className="w-full sm:w-auto bg-black text-white font-semibold cursor-pointer p-2 rounded-md border-none focus:outline-none focus:ring-2 focus:ring-custom-pink"
                    >
                        <option value="" disabled>
                            Select Gender
                        </option>
                        <option value="men">Men</option>
                        <option value="women">Women</option>
                    </select>
                </div>

                {/* Display Selected Option */}
                <div className="text-center sm:mt-0">
                    {selectedOption && (
                        <p className="text-lg font-medium">
                            You selected: <span className="font-bold">{selectedOption}</span>
                        </p>
                    )}
                </div>
            </div>

            {/* Products Section */}
            <div className="mt-10 relative">
                <div
                    ref={scrollerRef}
                    className="flex overflow-x-auto gap-6 scrollbar-hide py-4"
                    style={{
                        scrollbarWidth: "none",
                        msOverflowStyle: "none",
                    }}
                >
                    {products.map((product) => (
                        <div
                            key={product.id}
                            className="min-w-[250px] max-w-[300px] flex-shrink-0 bg-white shadow-lg rounded-lg p-4"
                        >
                            <img
                                src={product.imageUrl}
                                alt={product.name}
                                className="w-full h-[200px] rounded-lg object-cover mb-4"
                            />
                            <h1 className="text-lg font-bold mb-2">{product.name}</h1>
                            <p className="text-sm text-gray-500 mb-4">
                                {product.description}
                            </p>
                            <h1 className="text-base font-semibold mb-4">
                                Price: ${product.price}
                            </h1>
                            <button className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition duration-300">
                                Add to Cart
                            </button>
                        </div>
                    ))}
                </div>

                {/* Scroll Buttons */}
                <div className="flex justify-end gap-2 mt-3 pr-20">
                    <button
                        onClick={scrollLeft}
                        className="p-1 bg-gray-200 rounded-full hover:bg-gray-300 hover:scale-110 transition-all duration-200 shadow-sm"
                    >
                        <img
                            src="https://img.icons8.com/?size=24&id=26146&format=png"
                            alt="Scroll Left"
                            className="w-10 h-10"
                        />
                    </button>
                    <button
                        onClick={scrollRight}
                        className="p-1 bg-gray-200 rounded-full hover:bg-gray-300 hover:scale-110 transition-all duration-200 shadow-sm"
                    >
                        <img
                            src="https://img.icons8.com/?size=24&id=26147&format=png"
                            alt="Scroll Right"
                            className="w-10 h-10"
                        />
                    </button>
                </div>
            </div>
        </div>
    );
};

export default Products;
