import React, { useState, useRef, useEffect } from "react";
import { collection, getDocs, addDoc } from "firebase/firestore";
import { db } from "../../firebase/connection";

const Products = () => {
    const [products, setProducts] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [quantity, setQuantity] = useState(1);
    const [size, setSize] = useState("5ml");
    const [phone, setPhone] = useState("");
    const [userName, setUserName] = useState("");
    const [cart, setCart] = useState([]);
    const [cartModalOpen, setCartModalOpen] = useState(false);
    const scrollerRef = useRef(null);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const querySnapshot = await getDocs(collection(db, "products"));
                const productsData = querySnapshot.docs.map((doc) => ({
                    id: doc.id,
                    ...doc.data(),
                }));
                setProducts(productsData);
                setFilteredProducts(productsData);
            } catch (error) {
                console.error("Error fetching products: ", error);
            }
        };

        fetchProducts();
    }, []);

    const handleSearchChange = (e) => {
        const value = e.target.value;
        setSearchTerm(value);

        if (value) {
            const filteredData = products.filter((product) =>
                product.name.toLowerCase().includes(value.toLowerCase())
            );
            setFilteredProducts(filteredData);
        } else {
            setFilteredProducts(products);
        }
    };

    const handleAddToCart = () => {
        if (selectedProduct) {
            setCart((prevCart) => [
                ...prevCart,
                { ...selectedProduct, quantity, size },
            ]);
            alert(`${selectedProduct.name} added to the cart!`);
            setSelectedProduct(null);
            setQuantity(1);
            setSize("5ml");
        }
    };

    const handleOrderSubmit = async () => {
        if (!userName.trim()) {
            alert("Please enter your name.");
            return;
        }
        if (!phone.trim() || !/^\d{10,}$/.test(phone)) {
            alert("Please enter a valid phone number.");
            return;
        }
        if (cart.length === 0) {
            alert("Your cart is empty!");
            return;
        }

        try {
            for (const item of cart) {
                await addDoc(collection(db, "orders"), {
                    userName: userName.trim(),
                    phone: phone.trim(),
                    productName: item.name,
                    size: item.size,
                    quantity: item.quantity,
                    timestamp: new Date().toISOString(),
                });
            }
            alert("All orders placed successfully!");
            setCart([]);
            setCartModalOpen(false);
            setUserName("");
            setPhone("");
        } catch (error) {
            console.error("Error placing order: ", error);
            alert("Failed to place the order. Please try again later.");
        }
    };

    const scrollLeft = () => {
        if (scrollerRef.current) {
            scrollerRef.current.scrollBy({ left: -200, behavior: "smooth" });
        }
    };

    const scrollRight = () => {
        if (scrollerRef.current) {
            scrollerRef.current.scrollBy({ left: 200, behavior: "smooth" });
        }
    };

    return (
        <div id="products" className="pt-5 px-4">
            {/* Main Products Component */}
            <div className="flex justify-center">
                <h1 className="text-2xl sm:text-3xl lg:text-4xl font-charm text-center">
                    Plupare Products
                </h1>
            </div>

            {/* Search */}
            <div className="flex w-[80%] mx-auto pt-5">
                <input
                    type="text"
                    value={searchTerm}
                    onChange={handleSearchChange}
                    className="flex-grow border-2 border-gray-300 rounded-l-md p-2 text-black focus:outline-none focus:ring-2 focus:ring-gray-400"
                    placeholder="Search for a product..."
                />
                <button
                    type="submit"
                    className="bg-gray-300 px-4 py-2 rounded-r-md border-l-2 border-gray-400 hover:scale-110 transform transition-transform duration-200 ease-in-out focus:ring-2 focus:ring-gray-400 focus:outline-none"
                >
                    <img
                        src="https://cdn-icons-png.flaticon.com/128/775/775307.png"
                        alt="Search Icon"
                        className="h-6 w-6"
                    />
                </button>
            </div>

            {/* Products Section */}
            <div className="mt-10 relative">
                <div
                    ref={scrollerRef}
                    className="flex overflow-x-auto gap-6 scrollbar-hide py-4"
                    style={{
                        scrollbarWidth: "none",
                        msOverflowStyle: "none",
                    }}
                >
                    {filteredProducts.map((product) => (
                        <div
                            key={product.id}
                            className="min-w-[250px] sm:min-w-[300px] md:min-w-[350px] lg:min-w-[400px] flex-shrink-0 flex justify-center items-center flex-col shadow-lg rounded-lg p-6 border-4 border-white"
                        >
                            <img
                                src={product.imageUrl}
                                alt={product.name}
                                className="w-full h-[200px] sm:h-[250px] md:h-[300px] rounded-lg object-cover mb-6"
                            />
                            <h1 className="text-xl font-bold font-charm mb-4">
                                {product.name}
                            </h1>
                            <p className="text-base text-gray-500 mb-6">
                                {product.description}
                            </p>
                            <h1 className="text-lg font-semibold mb-6">
                                {product.price}
                            </h1>
                            <button
                                onClick={() => setSelectedProduct(product)}
                                className="text-white px-6 py-3 border-white border-2 rounded-lg hover:bg-gray-600 transition duration-300"
                            >
                                VIEW PRODUCT
                            </button>
                        </div>
                    ))}
                </div>
            </div>

            {/* Fixed Cart Button */}
            <button
                onClick={() => setCartModalOpen(true)}
                className="fixed bottom-10 right-1/2 translate-x-1/2 bg-gray-800 text-white w-[100px] h-[100px] rounded-full shadow-lg hover:bg-gray-700 transition duration-300 z-50"
            >
                Cart
            </button>

            {/* Cart Modal */}
            {cartModalOpen && (
                <div className="fixed inset-0 bg-black bg-opacity-70 flex justify-center items-center">
                    <div className="bg-white rounded-lg p-6 w-[90%] max-w-lg space-y-4">
                        <h2 className="text-xl font-bold">Your Cart</h2>
                        {cart.length > 0 ? (
                            <>
                                <ul className="space-y-2">
                                    {cart.map((item, index) => (
                                        <li key={index} className="flex justify-between">
                                            <span>{item.name}</span>
                                            <span>
                                                {item.quantity}x {item.size}
                                            </span>
                                        </li>
                                    ))}
                                </ul>
                                <button
                                    onClick={handleOrderSubmit}
                                    className="w-full bg-gray-800 text-white py-2 rounded hover:bg-gray-900"
                                >
                                    Submit Order
                                </button>
                            </>
                        ) : (
                            <p>Your cart is empty.</p>
                        )}
                        <button
                            onClick={() => setCartModalOpen(false)}
                            className="w-full bg-red-500 text-white py-2 rounded hover:bg-red-600"
                        >
                            Close
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Products;
