import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [selectedOption, setSelectedOption] = useState("");
  const navigate = useNavigate(); // Use useNavigate for navigation

  const toggleMenu = () => setMenuOpen(!menuOpen);

  const handleSelectChange = (e) => {
    const value = e.target.value;
    setSelectedOption(value);
    if (value) {
      navigate(`/${value}`);
    }
  };

  return (
    <header className="px-2 w-[100%]  h-20 flex items-center justify-between mx-auto  flex-wrap bg-gray-100 bg-opacity-30 backdrop-blur-md">
      <div className="flex justify-center items-center">
          <a href="/">
            <img
              src="/name.png"
              alt="logo"
              className="-mt-1 ml-5 max-w-[250px] w-full"

            />
          </a>
      </div>

      {/* Navigation Menu */}
      <nav className="flex justify-center items-center mr-5">
        <ul
          className={`absolute left-0 -top-3 w-full h-screen-[400] bg-black pr-3 flex flex-col items-center justify-center space-y-6 transform transition-transform duration-300 ease-in-out  ${menuOpen ? "translate-x-0 fixed -top-3 left-0 right-0 bottom-0 z-40 h-52" : "hidden"
            } sm:static sm:h-auto sm:w-auto sm:bg-transparent sm:flex sm:space-y-0 sm:space-x-6 sm:translate-x-0 sm:flex-row sm:text-xl`}
        >
          <li>
            <Link to="/career" onClick={toggleMenu}>
              <span className="text-white font-semibold hover:text-custom-pink cursor-pointer">
                Career
              </span>
            </Link>
          </li>
          <li>
            <Link to="/career" onClick={toggleMenu}>
              <span className="text-white font-semibold hover:text-custom-pink cursor-pointer">
                Men
              </span>
            </Link>
          </li>
          <li>
            <Link to="/career" onClick={toggleMenu}>
              <span className="text-white font-semibold hover:text-custom-pink cursor-pointer">
                Women
              </span>
            </Link>
          </li>

          <li>
            <Link to="/contact" onClick={toggleMenu}>
              <span className="text-white font-semibold hover:text-custom-pink cursor-pointer">
                Contact
              </span>
            </Link>
          </li>
        </ul>
      </nav>

      {/* Hamburger Menu */}
      <div
        className="z-50 mr-5 flex flex-col justify-center items-center sm:hidden cursor-pointer"
        onClick={toggleMenu}
      >
        <span
          className={`h-0.5 w-7 mb-1.5 bg-white transform transition-transform duration-300 ${menuOpen ? "rotate-45 translate-y-2" : ""}`}
        ></span>
        <span
          className={`h-0.5 w-7 mb-1.5 bg-white transition ${menuOpen ? "opacity-0" : ""}`}
        ></span>
        <span
          className={`h-0.5 w-7 mb-1.5 bg-white transform transition-transform duration-300 ${menuOpen ? "-rotate-45 -translate-y-2" : ""}`}
        ></span>
      </div>
    </header>
  );
};

export default Navbar;
