import React, { useState, useEffect } from "react";
import { addDoc, collection, getDocs, deleteDoc, doc } from "firebase/firestore";
import { db } from "../../firebase/connection";

const Admin = () => {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [loading, setLoading] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [products, setProducts] = useState([]);
  const [showOrders, setShowOrders] = useState(false);
  const [orders, setOrders] = useState([]);

  // Fetch products
  const fetchProducts = async () => {
    const productsCollection = collection(db, "products");
    const snapshot = await getDocs(productsCollection);
    const productList = snapshot.docs.map((doc) => ({
      id: doc.id,
      ...doc.data(),
    }));
    setProducts(productList);
  };

  // Fetch orders
  const fetchOrders = async () => {
    const ordersCollection = collection(db, "orders");
    const snapshot = await getDocs(ordersCollection);
    const orderList = snapshot.docs.map((doc) => ({
      id: doc.id,
      ...doc.data(),
    }));
    setOrders(orderList);
  };

  // Add product
  const handleSubmitProduct = async (e) => {
    e.preventDefault();
    if (!name || !description || !price || !imageUrl) {
      alert("Please fill in all fields.");
      return;
    }

    setLoading(true);
    try {
      const productsCollection = collection(db, "products");
      await addDoc(productsCollection, { name, description, price, imageUrl });
      alert("Product added successfully!");
      setName("");
      setDescription("");
      setPrice("");
      setImageUrl("");
      fetchProducts(); // Refresh products
    } catch (error) {
      console.error("Error adding product:", error.message);
      alert("Failed to add product.");
    } finally {
      setLoading(false);
    }
  };

  // Delete product
  const handleDeleteProduct = async (productId) => {
    try {
      await deleteDoc(doc(db, "products", productId));
      alert("Product deleted successfully!");
      fetchProducts(); // Refresh products
    } catch (error) {
      console.error("Error deleting product:", error.message);
      alert("Failed to delete product.");
    }
  };

  // Delete order
  const handleDeleteOrder = async (orderId) => {
    try {
      await deleteDoc(doc(db, "orders", orderId));
      alert("Order deleted successfully!");
      fetchOrders(); // Refresh orders
    } catch (error) {
      console.error("Error deleting order:", error.message);
      alert("Failed to delete order.");
    }
  };

  // Initial data fetch
  useEffect(() => {
    fetchProducts();
    fetchOrders();
  }, []);

  return (
    <div className="max-w-lg mx-auto p-5">
      <h1 className="text-3xl font-bold text-center mb-6">Admin Panel</h1>

      {/* Toggle buttons */}
      <div className="flex gap-4 mb-6">
        <button
          className="bg-blue-500 text-white px-6 py-2 rounded-md hover:bg-blue-600 transition"
          onClick={() => setShowForm(!showForm)}
        >
          {showForm ? "Hide Product Form" : "Add Product"}
        </button>
        <button
          className="bg-green-500 text-white px-6 py-2 rounded-md hover:bg-green-600 transition"
          onClick={() => setShowOrders(!showOrders)}
        >
          {showOrders ? "Hide Orders" : "View Orders"}
        </button>
      </div>

      {/* Product Form */}
      {showForm && (
        <form className="bg-gray-100 p-6 rounded-lg shadow-md" onSubmit={handleSubmitProduct}>
          <div className="mb-4">
            <label htmlFor="name" className="block text-gray-700 font-medium mb-2">Product Name</label>
            <input
              id="name"
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="w-full border border-gray-300 rounded-md p-2 focus:ring-2 focus:ring-blue-500 text-black"
              required
            />
          </div>
          <div className="mb-4">
            <label htmlFor="description" className="block text-gray-700 font-medium mb-2">Description</label>
            <textarea
              id="description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full border border-gray-300 rounded-md p-2 focus:ring-2 focus:ring-blue-500 text-black"
              required
            />
          </div>
          <div className="mb-4">
            <label htmlFor="price" className="block text-gray-700 font-medium mb-2">Price</label>
            <input
              id="price"
              type="text"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
              className="w-full border border-gray-300 rounded-md p-2 focus:ring-2 focus:ring-blue-500 text-black"
              required
            />
          </div>
          <div className="mb-4">
            <label htmlFor="imageUrl" className="block text-gray-700 font-medium mb-2">Image URL</label>
            <input
              id="imageUrl"
              type="url"
              value={imageUrl}
              onChange={(e) => setImageUrl(e.target.value)}
              className="w-full border border-gray-300 rounded-md p-2 focus:ring-2 focus:ring-blue-500 text-black"
              required
            />
          </div>
          <button
            type="submit"
            className={`w-full bg-green-500 text-white py-2 rounded-md hover:bg-green-600 transition ${loading ? "opacity-50 cursor-not-allowed" : ""}`}
            disabled={loading}
          >
            {loading ? "Adding..." : "Add Product"}
          </button>
        </form>
      )}

      {/* Products List */}
      <div className="mt-6">
        <h2 className="text-2xl font-bold mb-4">Products</h2>
        {products.map((product) => (
          <div key={product.id} className="p-4 bg-gray-200 rounded-md mb-2 flex justify-between items-center">
            <div>
              <h3 className="font-bold">{product.name}</h3>
              <p>{product.description}</p>
              <p className="text-green-600 font-bold">${product.price}</p>
            </div>
            <button
              onClick={() => handleDeleteProduct(product.id)}
              className="bg-red-500 text-white px-3 py-1 rounded-md hover:bg-red-600 transition"
            >
              Delete
            </button>
          </div>
        ))}
      </div>

      {/* Orders List */}
      {showOrders && (
        <div className="mt-6">
          <h2 className="text-2xl font-bold mb-4">Orders</h2>
          {orders.map((order) => (
            <div key={order.id} className="p-4 bg-gray-200 rounded-md mb-2 flex justify-between items-center">
              <div>
                <h3 className="font-bold">Order #{order.id}</h3>
                <p>{order.details}</p>
              </div>
              <button
                onClick={() => handleDeleteOrder(order.id)}
                className="bg-red-500 text-white px-3 py-1 rounded-md hover:bg-red-600 transition"
              >
                Delete
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Admin;
