import React, { useState, useEffect } from "react";
import { addDoc, collection, getDocs, deleteDoc, doc } from "firebase/firestore";
import { db } from "../../firebase/connection";

const Admin = () => {
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [showProducts, setShowProducts] = useState(false);
  const [showOrders, setShowOrders] = useState(false);

  // Fetch all products
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const querySnapshot = await getDocs(collection(db, "products"));
        const productsData = querySnapshot.docs.map((doc) => ({
          id: doc.id,
          ...doc.data(),
        }));
        setProducts(productsData);
      } catch (error) {
        console.error("Error fetching products: ", error.message);
      }
    };

    fetchProducts();
  }, []);

  // Fetch orders when "View Orders" is clicked
  const fetchOrders = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, "orders"));
      const ordersData = querySnapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setOrders(ordersData);
      setShowOrders(true);
    } catch (error) {
      console.error("Error fetching orders: ", error.message);
    }
  };

  // Delete a product
  const deleteProduct = async (id) => {
    try {
      await deleteDoc(doc(db, "products", id));
      setProducts((prev) => prev.filter((product) => product.id !== id));
      alert("Product deleted successfully!");
    } catch (error) {
      console.error("Error deleting product: ", error.message);
      alert("Failed to delete product.");
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-5">
      <h1 className="text-3xl font-bold text-center mb-6">Admin Panel</h1>

      {/* Buttons to toggle views */}
      <div className="flex gap-4 mb-6 justify-center">
        <button
          className="bg-blue-500 text-white px-6 py-2 rounded-md hover:bg-blue-600 transition"
          onClick={() => setShowProducts(!showProducts)}
        >
          {showProducts ? "Hide Products" : "View Products"}
        </button>
        <button
          className="bg-green-500 text-white px-6 py-2 rounded-md hover:bg-green-600 transition"
          onClick={fetchOrders}
        >
          {showOrders ? "Hide Orders" : "View Orders"}
        </button>
      </div>

      {/* Display Products */}
      {showProducts && (
        <div className="bg-gray-100 p-6 rounded-lg shadow-md mb-6">
          <h2 className="text-xl font-bold mb-4">Products</h2>
          <ul>
            {products.map((product) => (
              <li key={product.id} className="mb-4 border-b pb-2">
                <p><strong>Name:</strong> {product.name}</p>
                <p><strong>Description:</strong> {product.description}</p>
                <p><strong>Price:</strong> ${product.price}</p>
                <button
                  className="mt-2 bg-red-500 text-white px-4 py-1 rounded-md hover:bg-red-600 transition"
                  onClick={() => deleteProduct(product.id)}
                >
                  Delete Product
                </button>
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Display Orders */}
      {showOrders && (
        <div className="bg-gray-100 p-6 rounded-lg shadow-md">
          <h2 className="text-xl font-bold mb-4">Orders</h2>
          <ul>
            {orders.map((order) => (
              <li key={order.id} className="mb-4 border-b pb-2">
                <p><strong>Order ID:</strong> {order.id}</p>
                <p><strong>Customer Name:</strong> {order.customerName || "N/A"}</p>
                <p><strong>Total:</strong> ${order.total || "N/A"}</p>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Admin;
