import React, { useState, useRef, useEffect } from "react";
import { collection, getDocs, addDoc } from "firebase/firestore";
import { db } from "../../firebase/connection";

const Products = () => {
    const [products, setProducts] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [quantity, setQuantity] = useState(1);
    const [size, setSize] = useState("5ml");
    const [cart, setCart] = useState([]);
    const [cartModalOpen, setCartModalOpen] = useState(false);
    const [userName, setUserName] = useState("");
    const [address, setAddress] = useState("");
    const [phone, setPhone] = useState("");
    const scrollerRef = useRef(null);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const querySnapshot = await getDocs(collection(db, "products"));
                const productsData = querySnapshot.docs.map((doc) => ({
                    id: doc.id,
                    ...doc.data(),
                }));
                setProducts(productsData);
                setFilteredProducts(productsData);
            } catch (error) {
                console.error("Error fetching products: ", error);
            }
        };

        fetchProducts();
    }, []);

    const handleAddToCart = () => {
        const cartItem = {
            name: selectedProduct.name,
            size,
            quantity,
        };
        setCart([...cart, cartItem]);
        setSelectedProduct(null); // Close the product modal
    };

    const handleSendOrder = async () => {
        if (userName && address && phone && cart.length > 0) {
            try {
                await addDoc(collection(db, "orders"), {
                    userName,
                    address,
                    phone,
                    cart,
                });
                alert("Order placed successfully!");
                setCart([]);
                setCartModalOpen(false);
                setUserName("");
                setAddress("");
                setPhone("");
            } catch (error) {
                console.error("Error sending order: ", error);
            }
        } else {
            alert("Please fill out all fields and add products to your cart.");
        }
    };

    const handleSearchChange = (e) => {
        const value = e.target.value;
        setSearchTerm(value);

        if (value) {
            const filteredData = products.filter((product) =>
                product.name.toLowerCase().includes(value.toLowerCase())
            );
            setFilteredProducts(filteredData);
        } else {
            setFilteredProducts(products);
        }
    };

    const scrollLeft = () => {
        if (scrollerRef.current) {
            scrollerRef.current.scrollBy({ left: -200, behavior: "smooth" });
        }
    };

    const scrollRight = () => {
        if (scrollerRef.current) {
            scrollerRef.current.scrollBy({ left: 200, behavior: "smooth" });
        }
    };

    return (
        <div id="products" className="pt-5 px-4">
            {/* Cart Button */}
            <button
                onClick={() => setCartModalOpen(true)}
                className="fixed bottom-10 right-1/2 translate-x-1/2 bg-gray-800 text-white w-[80px] h-[80px] rounded-full shadow-lg border-2 border-dashed border-white hover:bg-gray-700 transition duration-300 z-50"
            >
                Cart
            </button>

            {/* Cart Modal */}
            {cartModalOpen && (
                <div className="fixed inset-0 bg-black bg-opacity-80 flex justify-center items-center z-50">
                    <div className="bg-gray-900 text-white w-[90%] max-w-5xl p-6 rounded-lg shadow-lg flex">
                        {/* Left: Cart Items */}
                        <div className="w-1/2 pr-4 border-r border-gray-700">
                            <h2 className="text-2xl font-bold mb-4">Your Cart</h2>
                            {cart.length > 0 ? (
                                <ul className="space-y-4">
                                    {cart.map((item, index) => (
                                        <li
                                            key={index}
                                            className="flex justify-between items-center border-b border-gray-700 pb-2"
                                        >
                                            <div>
                                                <h3 className="text-lg font-semibold">
                                                    {item.name}
                                                </h3>
                                                <p className="text-sm">Size: {item.size}</p>
                                                <p className="text-sm">Quantity: {item.quantity}</p>
                                            </div>
                                            <button
                                                onClick={() =>
                                                    setCart(cart.filter((_, i) => i !== index))
                                                }
                                                className="text-red-500 hover:text-red-600"
                                            >
                                                Remove
                                            </button>
                                        </li>
                                    ))}
                                </ul>
                            ) : (
                                <p>Your cart is empty.</p>
                            )}
                        </div>

                        {/* Right: Order Form */}
                        <div className="w-1/2 pl-4">
                            <h2 className="text-2xl font-bold mb-4">Order Details</h2>
                            <form
                                onSubmit={(e) => {
                                    e.preventDefault();
                                    handleSendOrder();
                                }}
                                className="space-y-4"
                            >
                                <div>
                                    <label className="block text-white mb-1">Name</label>
                                    <input
                                        type="text"
                                        value={userName}
                                        onChange={(e) => setUserName(e.target.value)}
                                        className="w-full text-black border-gray-300 rounded p-2"
                                        placeholder="Enter your name"
                                    />
                                </div>
                                <div>
                                    <label className="block text-white mb-1">Address</label>
                                    <textarea
                                        value={address}
                                        onChange={(e) => setAddress(e.target.value)}
                                        className="w-full text-black border-gray-300 rounded p-2"
                                        placeholder="Enter your address"
                                    />
                                </div>
                                <div>
                                    <label className="block text-white mb-1">Phone</label>
                                    <input
                                        type="text"
                                        value={phone}
                                        onChange={(e) => setPhone(e.target.value)}
                                        className="w-full text-black border-gray-300 rounded p-2"
                                        placeholder="Enter your phone number"
                                    />
                                </div>
                                <button
                                    type="submit"
                                    className="w-full bg-green-500 hover:bg-green-600 text-white py-2 rounded"
                                >
                                    Send Order
                                </button>
                                <button
                                    onClick={() => setCartModalOpen(false)}
                                    className="w-full bg-red-500 hover:bg-red-600 text-white py-2 rounded mt-2"
                                >
                                    Close
                                </button>
                            </form>
                        </div>
                    </div>
                </div>
            )}

            {/* Search Bar */}
            <div className="flex w-[80%] mx-auto pt-5">
                <input
                    type="text"
                    value={searchTerm}
                    onChange={handleSearchChange}
                    className="flex-grow border-2 border-gray-300 rounded-l-md p-2 text-black focus:outline-none focus:ring-2 focus:ring-gray-400"
                    placeholder="Search for a product..."
                />
                <button
                    type="submit"
                    className="bg-gray-300 px-4 py-2 rounded-r-md border-l-2 border-gray-400 hover:scale-110 transform transition-transform duration-200 ease-in-out focus:ring-2 focus:ring-gray-400 focus:outline-none"
                >
                    <img
                        src="https://cdn-icons-png.flaticon.com/128/775/775307.png"
                        alt="Search Icon"
                        className="h-6 w-6"
                    />
                </button>
            </div>

            {/* Products Section */}
            <div className="mt-10 relative">
                <div
                    ref={scrollerRef}
                    className="flex overflow-x-auto gap-6 scrollbar-hide py-4"
                >
                    {filteredProducts.map((product) => (
                        <div
                            key={product.id}
                            className="min-w-[300px] flex-shrink-0 flex flex-col items-center shadow-lg rounded-lg p-6 border-4 border-white"
                        >
                            <img
                                src={product.imageUrl}
                                alt={product.name}
                                className="w-full h-[200px] object-cover mb-6"
                            />
                            <h1 className="text-xl font-bold mb-4">{product.name}</h1>
                            <p className="text-base text-gray-500 mb-6">
                                {product.description}
                            </p>
                            <h1 className="text-lg font-semibold mb-6">
                                ${product.price}
                            </h1>
                            <button
                                onClick={() => setSelectedProduct(product)}
                                className="text-white px-6 py-3 border-white border-2 rounded-lg hover:bg-gray-600 transition duration-300"
                            >
                                VIEW PRODUCT
                            </button>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default Products;
