import { useState } from "react";

const Headads = () => {
  return (
    <div className="flex flex-col gap-6">
      {/* Heading */}
      <h1 className="pt-10 text-lg sm:text-xl md:text-3xl lg:text-6xl flex justify-center font-bold font-charm text-center px-4">
        Let your presence linger with our captivating aromas.
      </h1>
      {/* Image Section */}
      <div className="h-[30vh] sm:h-[35vh] md:h-[40vh] lg:h-[69vh] flex justify-around items-center gap-4 px-4 pt-5">
        {/* First Image */}
        <div className="relative h-[100%] w-[30%] group">
          <h1 className="absolute bottom-2 right-2 text-white text-xl lg:text-base font-bold font-charm z-10 px-4 py-2 rounded-md opacity-0 group-hover:opacity-100 transition-opacity duration-300 ease-in-out">
            La Nuit de L'Homme, Yves Saint Laurent
          </h1>
          <img
            src="https://i.pinimg.com/736x/75/fd/8f/75fd8f9ab905b616eaf857252898dfcc.jpg"
            alt="Image One"
            className="w-full h-full rounded-3xl border-2 border-white group-hover:scale-105 transition-transform duration-300 ease-in-out"
          />
        </div>
        {/* Second Image */}
        <div className="relative h-[100%] w-[30%] group">
          <h1 className="absolute bottom-2 right-2 text-white text-xl lg:text-base font-bold font-charm z-10 px-4 py-2 rounded-md opacity-0 group-hover:opacity-100 transition-opacity duration-300 ease-in-out">
            Sauvage Elixir, DIOR
          </h1>
          <img
            src="https://i.pinimg.com/736x/95/f9/a0/95f9a0c4727c2e8c96acd836c031363d.jpg"
            alt="Image Two"
            className="w-full h-full rounded-3xl border-2 border-white group-hover:scale-105 transition-transform duration-300 ease-in-out"
          />
        </div>
      </div>

    </div>
  );
};

export default Headads;
