import React, { useState, useRef, useEffect } from "react";
import { collection, getDocs, addDoc } from "firebase/firestore";
import { db } from "../../firebase/connection";

const Products = () => {
    const [products, setProducts] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [quantity, setQuantity] = useState(1);
    const [size, setSize] = useState("5ml");
    const [phone, setPhone] = useState("");
    const [userName, setUserName] = useState("");
    const scrollerRef = useRef(null);
    const [cart, setCart] = useState([]);
    const [cartModalOpen, setCartModalOpen] = useState(false);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const querySnapshot = await getDocs(collection(db, "products"));
                const productsData = querySnapshot.docs.map((doc) => ({
                    id: doc.id,
                    ...doc.data(),
                }));
                setProducts(productsData);
                setFilteredProducts(productsData);
            } catch (error) {
                console.error("Error fetching products: ", error);
            }
        };

        fetchProducts();
    }, []);

    const handleAddToCart = () => {
        const cartItem = {
            name: selectedProduct.name,
            size,
            quantity,
        };
        setCart([...cart, cartItem]);
        setSelectedProduct(null); // Close the product modal
    };

    const handleSearchChange = (e) => {
        const value = e.target.value;
        setSearchTerm(value);

        if (value) {
            const filteredData = products.filter((product) =>
                product.name.toLowerCase().includes(value.toLowerCase())
            );
            setFilteredProducts(filteredData);
        } else {
            setFilteredProducts(products);
        }
    };

    const scrollLeft = () => {
        if (scrollerRef.current) {
            scrollerRef.current.scrollBy({ left: -200, behavior: 'smooth' });
        }
    };

    const scrollRight = () => {
        if (scrollerRef.current) {
            scrollerRef.current.scrollBy({ left: 200, behavior: 'smooth' });
        }
    };

    return (
        <div id="products" className="pt-5 px-4">
            {/* Cart Button */}
            <button
                onClick={() => setCartModalOpen(true)}
                className="fixed bottom-10 right-1/2 translate-x-1/2 bg-gray-800 text-white w-[80px] h-[80px] rounded-full shadow-lg border-2 border-dashed border-white hover:bg-gray-700 transition duration-300 z-50"
            >
                Cart
            </button>

            {/* Cart Modal */}
            {cartModalOpen && (
                <div className="fixed inset-0 bg-black bg-opacity-80 flex justify-center items-center z-50">
                    <div className="bg-gray-900 text-white w-[90%] max-w-lg p-6 rounded-lg shadow-lg">
                        <h2 className="text-2xl font-bold mb-4">Your Cart</h2>
                        {cart.length > 0 ? (
                            <ul className="space-y-4">
                                {cart.map((item, index) => (
                                    <li key={index} className="flex justify-between items-center border-b border-gray-700 pb-2">
                                        <div>
                                            <h3 className="text-lg font-semibold">{item.name}</h3>
                                            <p className="text-sm">Size: {item.size}</p>
                                            <p className="text-sm">Quantity: {item.quantity}</p>
                                        </div>
                                        <button
                                            onClick={() =>
                                                setCart(cart.filter((_, i) => i !== index))
                                            }
                                            className="text-red-500 hover:text-red-600"
                                        >
                                            Remove
                                        </button>
                                    </li>
                                ))}
                            </ul>
                        ) : (
                            <p>Your cart is empty.</p>
                        )}
                        <button
                            onClick={() => setCartModalOpen(false)}
                            className="mt-6 w-full bg-red-500 hover:bg-red-600 text-white py-2 rounded"
                        >
                            Close
                        </button>
                    </div>
                </div>
            )}

            {/* Products Section */}
            {/* Existing code for products display */}
            {/* ... */}

            {/* Product Modal */}
            {selectedProduct && (
                <div className="fixed inset-0 bg-black bg-opacity-70 flex justify-center items-center">
                    <div className="bg-black p-6 rounded-lg shadow-lg w-[90%] max-w-4xl flex">
                        <div className="w-[50%] flex justify-center items-center">
                            <img
                                src={selectedProduct.imageUrl}
                                alt={selectedProduct.name}
                                className="w-full h-full object-cover rounded-lg"
                            />
                        </div>
                        <div className="w-[50%] flex flex-col justify-between pl-6">
                            <h1 className="text-2xl font-bold mb-4">{selectedProduct.name}</h1>
                            <form
                                onSubmit={(e) => {
                                    e.preventDefault();
                                    handleAddToCart();
                                }}
                                className="space-y-4"
                            >
                                {/* Form fields */}
                                <button
                                    type="submit"
                                    className="w-full bg-gray-800 text-white py-2 rounded hover:bg-gray-900"
                                >
                                    Add to Cart
                                </button>
                                <button
                                    onClick={() => setSelectedProduct(null)}
                                    className="w-full bg-red-500 text-white py-2 rounded hover:bg-red-600 mt-2"
                                >
                                    Close
                                </button>
                            </form>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Products;
