import React, { useState, useRef, useEffect, useCallback } from "react";
import { collection, getDocs, addDoc } from "firebase/firestore";
import { db } from "../../firebase/connection";

const Products = () => {
    const [products, setProducts] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [quantity, setQuantity] = useState(1);
    const [size, setSize] = useState("5ml");
    const [phone, setPhone] = useState("");
    const [userName, setUserName] = useState("");
    const scrollerRef = useRef(null);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const querySnapshot = await getDocs(collection(db, "products"));
                const productsData = querySnapshot.docs.map((doc) => ({
                    id: doc.id,
                    ...doc.data(),
                }));
                setProducts(productsData);
                setFilteredProducts(productsData);
            } catch (error) {
                console.error("Error fetching products: ", error);
                alert("Failed to load products. Please try again later.");
            }
        };

        fetchProducts();
    }, []);

    const handleSearchChange = (e) => {
        const value = e.target.value.toLowerCase();
        setSearchTerm(value);
        setFilteredProducts(
            value
                ? products.filter((product) =>
                      product.name.toLowerCase().includes(value)
                  )
                : products
        );
    };

    const handleOrderSubmit = async () => {
        if (!userName.trim()) {
            alert("Please enter your name.");
            return;
        }
        if (!phone.trim() || !/^\d{10,}$/.test(phone)) {
            alert("Please enter a valid phone number.");
            return;
        }
        if (!quantity || quantity <= 0) {
            alert("Please enter a valid quantity.");
            return;
        }
        if (!selectedProduct) {
            alert("No product selected.");
            return;
        }

        try {
            await addDoc(collection(db, "orders"), {
                userName: userName.trim(),
                phone: phone.trim(),
                productName: selectedProduct.name,
                size,
                quantity,
                timestamp: new Date().toISOString(),
            });
            alert("Order placed successfully!");
            setSelectedProduct(null);
            setQuantity(1);
            setPhone("");
            setUserName("");
        } catch (error) {
            console.error("Error placing order: ", error);
            alert("Failed to place the order. Please try again later.");
        }
    };

    const scroll = useCallback(
        (direction) => {
            if (scrollerRef.current) {
                scrollerRef.current.scrollBy({
                    left: direction === "left" ? -200 : 200,
                    behavior: "smooth",
                });
            }
        },
        [scrollerRef]
    );

    return (
        <div id="products" className="pt-5 px-4">
            <div className="flex justify-center">
                <h1 className="text-2xl sm:text-3xl lg:text-4xl font-charm text-center">
                    Plupare Products
                </h1>
            </div>

            <div className="flex w-[80%] mx-auto pt-5">
                <input
                    type="text"
                    value={searchTerm}
                    onChange={handleSearchChange}
                    className="flex-grow border-2 border-gray-300 rounded-l-md p-2 text-black focus:outline-none focus:ring-2 focus:ring-gray-400"
                    placeholder="Search for a product..."
                />
                <button
                    type="submit"
                    className="bg-gray-300 px-4 py-2 rounded-r-md border-l-2 border-gray-400 hover:scale-110 transform transition-transform duration-200 ease-in-out focus:ring-2 focus:ring-gray-400 focus:outline-none"
                    aria-label="Search"
                >
                    <img
                        src="https://cdn-icons-png.flaticon.com/128/775/775307.png"
                        alt="Search Icon"
                        className="h-6 w-6"
                    />
                </button>
            </div>

            <div className="mt-10 relative">
                <div
                    ref={scrollerRef}
                    className="flex overflow-x-auto gap-6 py-4 scrollbar-hide"
                >
                    {filteredProducts.map((product) => (
                        <div
                            key={product.id}
                            className="min-w-[250px] flex-shrink-0 flex justify-center items-center flex-col shadow-lg rounded-lg p-6 border-4 border-white"
                        >
                            <img
                                src={product.imageUrl}
                                alt={product.name}
                                className="w-full h-[200px] rounded-lg object-cover mb-6"
                            />
                            <h1 className="text-xl font-bold font-charm mb-4">
                                {product.name}
                            </h1>
                            <p className="text-base text-gray-500 mb-6">
                                {product.description}
                            </p>
                            <h1 className="text-lg font-semibold mb-6">
                                {product.price}
                            </h1>
                            <button
                                onClick={() => setSelectedProduct(product)}
                                className="text-white px-6 py-3 border-white border-2 rounded-lg hover:bg-gray-600 transition duration-300"
                            >
                                VIEW PRODUCT
                            </button>
                        </div>
                    ))}
                </div>
            </div>

            <div className="flex justify-center gap-2 mt-3">
                <button
                    onClick={() => scroll("left")}
                    className="p-1 bg-gray-200 rounded-full hover:bg-gray-300 hover:scale-110 transition-all duration-200 shadow-sm"
                    aria-label="Scroll Left"
                >
                    <img
                        src="https://img.icons8.com/?size=24&id=26146&format=png"
                        alt="Scroll Left"
                        className="w-10 h-10"
                    />
                </button>
                <button
                    onClick={() => scroll("right")}
                    className="p-1 bg-gray-200 rounded-full hover:bg-gray-300 hover:scale-110 transition-all duration-200 shadow-sm"
                    aria-label="Scroll Right"
                >
                    <img
                        src="https://img.icons8.com/?size=24&id=26147&format=png"
                        alt="Scroll Right"
                        className="w-10 h-10"
                    />
                </button>
            </div>

            {selectedProduct && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
                    <div className="bg-white p-6 rounded-lg shadow-lg w-[90%] max-w-4xl flex">
                        <div className="w-[50%] flex justify-center items-center">
                            <img
                                src={selectedProduct.imageUrl}
                                alt={selectedProduct.name}
                                className="w-full h-full object-cover rounded-lg"
                            />
                        </div>

                        <div className="w-[50%] flex flex-col pl-6">
                            <h1 className="text-2xl font-bold mb-4">
                                {selectedProduct.name}
                            </h1>
                            <form
                                onSubmit={(e) => e.preventDefault()}
                                className="space-y-4"
                            >
                                <div>
                                    <label className="block text-gray-700 mb-1">
                                        User Name
                                    </label>
                                    <input
                                        type="text"
                                        value={userName}
                                        onChange={(e) =>
                                            setUserName(e.target.value)
                                        }
                                        className="w-full text-black border-gray-300 rounded p-2"
                                    />
                                </div>
                                <div>
                                    <label className="block text-gray-700 mb-1">
                                        Phone
                                    </label>
                                    <input
                                        type="text"
                                        value={phone}
                                        onChange={(e) =>
                                            setPhone(e.target.value)
                                        }
                                        className="w-full text-black border-gray-300 rounded p-2"
                                    />
                                </div>
                                <div>
                                    <label className="block text-gray-700 mb-1">
                                        Size
                                    </label>
                                    <select
                                        value={size}
                                        onChange={(e) =>
                                            setSize(e.target.value)
                                        }
                                        className="w-full text-black border-gray-300 rounded p-2"
                                    >
                                        <option value="5ml">5ml</option>
                                        <option value="10ml">10ml</option>
                                    </select>
                                </div>
                                <div>
                                    <label className="block text-gray-700 mb-1">
                                        Quantity
                                    </label>
                                    <input
                                        type="number"
                                        value={quantity}
                                        onChange={(e) =>
                                            setQuantity(Number(e.target.value))
                                        }
                                        className="w-full text-black border-gray-300 rounded p-2"
                                    />
                                </div>
                                <button
                                    onClick={handleOrderSubmit}
                                    className="bg-blue-500 text-white px-4 py-2 rounded mt-4 hover:bg-blue-600 transition duration-300"
                                >
                                    Submit Order
                                </button>
                            </form>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Products;
